import React, { Component } from 'react';
import ToDoItem from './ToDoItem';
import AddItem from './AddItem';

class ToDoListBox extends Component {
    constructor() {
        super()
        this.state = {
            items:[]
        }
        this.handleRemove = this.handleRemove.bind(this)
        this.handleAdd = this.handleRemove.bind(this)
        this.addItem = this.addItem.bind(this)
    }

    addItem(text) {
        const newItem = {
            text: text
        }
        let tempItems = this.state.items
        tempItems.push(newItem)
        this.setState({
            items: tempItems
        })
    }

    handleRemove(text) {
        let tempItem = null

        const tempItems = this.state.items.map(item => {
            if (text === item.text) {
                tempItem = item
            }
            return item
        })

        tempItems.splice(tempItems.indexOf(tempItem), 1)

        this.setState({
            items: tempItems
        })

    }

    render() {
        let toDoListItems = []

        return (
            <div class="jumbotron m-3 shadow p-3 z-1030">
                <h6 className="d-inline">To Do List</h6><button data-toggle="modal" data-target="#addItemModal" className="float-right btn btn-warning">Add Item</button>
                <div id="carouselExampleInterval" class="carousel slide" data-ride="carousel">
                    <div className="carousel-inner">
                        <div className="carousel-item active" data-interval="5000">
                            <div className="row mx-auto">
                                {toDoListItems}
                                <ToDoItem text="Take out the trash" remove={this.handleRemove}/>          
                            </div>
                        </div>
                    </div>
                    <a className="carousel-control-prev w-10" href="#carouselExampleInterval" role="button" data-slide="prev">
                        <span className="carousel-control-prev-icon" aria-hidden="true"></span>
                        <span className="sr-only">Previous</span>
                    </a>
                    <a className="carousel-control-next w-10" href="#carouselExampleInterval" role="button" data-slide="next">
                        <span className="carousel-control-next-icon" aria-hidden="true"></span>
                        <span className="sr-only">Next</span>
                    </a>
                </div>
                
            </div>
        );
    }
}

export default ToDoListBox;
