import axios from 'axios'
import { GET_ALL_COURSES } from './types';

export const getAllCourses =()=> dispatch => {
    axios.get("http://localhost:8080/course/all")
    .then(response => {
        console.log(response.data)
        dispatch({
            type: GET_ALL_COURSES,
            payload: response.data
        })
    })
}
