import React, { Component } from 'react';
import Header from '../../components/main_page/header/Header';
import './admin_page.css';
import UserHomeLayout from '../../layout/UserHomeLayout';
import axios from 'axios';

class AdminPage extends Component {
    constructor() {
        super()
        this.state = {
            userList: [],
            user: "",
            message: ""
        }
    }

    componentDidMount() {
        //Retrieve all user data and store it in state
        this.retireveUserData(sessionStorage.getItem("user"))
        axios.post("http://localhost:8080/all").then(response => {
            this.setState({
                userList: response.data
            })
        })
    }

    retireveUserData(email) {
        const user = {
            email: email
        }

        axios.post("http://localhost:8080/find", user)
            .then((response) => {
                console.log(response)
                if (response.data != "") {
                    this.setState({
                        user: response.data
                    })
                } else {
                    sessionStorage.clear()
                    this.setState({
                        message: "Error retrieving data, please logout and try again"
                    })
                }
            }
            )}

    render() {
        return (
            <div className="">
                <Header userList={this.state.userList} user={this.state.user} />
                {this.state.user ? <UserHomeLayout userList={this.state.userList} user={this.state.user} /> : <h3>{this.state.message}</h3> }
            </div>
        );
    }
}

export default AdminPage;
