import React, { Component } from 'react';
import LoginPage from '../pages/login/LoginPage'
import StudentPage from './../pages/student/StudentPage';
import AdminPage from '../pages/admin/AdminPage';
import {
    BrowserRouter as Router,
    NavLink,
    Switch,
    Route,
    Link,
    Redirect
  } from "react-router-dom";
import ProtectedLink from '../lib/ProtectedLink';



class Layout extends Component {
    render() {

        localStorage.setItem("userToken", true);
        localStorage.setItem("userType", "ADMIN");

        const token = localStorage.getItem("userToken")
        const userType = localStorage.getItem("userType")
        let to = "/";
        let component = LoginPage; 

        return (
            <Router>
                <div>
                    <Switch>
                        <Route path={["/", "login-student", "login-parent", "login-staff", "login", "home"]} exact component={LoginPage} />
                        {/* <Route path="/admin-home" component={AdminPage} /> */}

                        <Route path="/admin-home" component={AdminPage}>
                            {localStorage.getItem("userToken") ? <Redirect  to="admin-home"  /> : <Redirect to="/" /> } 
                        </Route>
                        

                    </Switch>
                    
                </div>
            </Router>
        );
    }
}

export default Layout;
