    //       {value: 'B', readOnly: true, width: 100},
    //       {value: 'C', readOnly: true, width: 100},
    //       {value: 'D', readOnly: true, width: 100}
    //     ],
    //     [{readOnly: true, value: 1}, {value: 1}, {value: 3}, {value: 3}, {value: 3}],
    //     [{readOnly: true, value: 2}, {value: 2}, {value: 4}, {value: 4}, {value: 4}],
    //     [{readOnly: true, value: 3}, {value: 1}, {value: 3}, {value: 3}, {value: 3}],
    //     [{readOnly: true, value: 4}, {value: 2}, {value: 4}, {value: 4}, {value: 4}]
    //   ]
    // }
  }

  componentWillReceiveProps() {

  }



  render () {
    //console.log(this.props.editCourse)
    //const headers = [ {}, {}]
    //provides all of the assignment headers
    let headers = [{readOnly: true, value: '', width: 100}]
    this && this.props && this.props.editCourse && this.props.editCourse && this.props.editCourse.assignments.forEach(assignment => {
      headers.push({readOnly: true, value: assignment.name + " " + assignment.totalPoints, width: 100})
    });

    console.log(this.props.editCourse)
    // this.props.editCourse && this.props.editCourse.students.map(student => {
    //   let count = 0;
    //   student.assignmentScores
    // })
    
    let students = [{readOnly: true, value: '', width: 100}]
    
   
    let tempGrid = [
      headers,
      [{readOnly: true, value: 1}, {value: 1}, {value: 3}, {value: 3}, {value: 3}],
      [{readOnly: true, value: 2}, {value: 2}, {value: 4}, {value: 4}, {value: 4}],
      [{readOnly: true, value: 3}, {value: 1}, {value: 3}, {value: 3}, {value: 3}],
      [{readOnly: true, value: 4}, {value: 2}, {value: 4}, {value: 4}, {value: 4}]
    ]

    return (
      // <div></div>
      <Datasheet
      className="table table-hover"
        data={tempGrid}
        valueRenderer={(cell) => cell.value}
        onContextMenu={(e, cell, i, j) => cell.readOnly ? e.preventDefault() : null}
        onCellsChanged={changes => {
          const grid = tempGrid.map(row => [...row])
          changes.forEach(({cell, row, col, value}) => {
            grid[row][col] = {...grid[row][col], value}
          })
          this.setState({grid})
        }}
      />
    )
  }
}

const mapStateToProps = state => ({
  editCourse: state.temp.editCourse,
  temp: state.temp.grid
})

export default connect(mapStateToProps, {})(Table)
