import React, { Component } from 'react';
import AddCourseForm from './AddCourseForm';
import EditCourseForm from './EditCourseForm';
import axios from 'axios'
import CourseItem from './CourseItem';
import Fade from 'react-reveal/Fade'

class AddCourseModal extends Component {
  constructor() {
    super()
    this.state = {

    }
  }

  editCourse(course) {

    document.getElementById("createCourseForm").style.display = "none";
    document.getElementById("editCourseForm").style.display = "block";

  }

  newCourse() {
    document.getElementById("createCourseForm").style.display = "block";
    document.getElementById("editCourseForm").style.display = "none";
  }


  saveCourse(course) {

    axios.post("http://localhost:8080/course/new", course).then(response => {
      let tempCourseList = this.props.courseList
      tempCourseList.push(response.data)
      this.props.updateCourseList(tempCourseList)
    })

  }

  render() {

    const courseItems = this.props.courseList.map(course => {
      return <CourseItem course={course} name={course.name} selectCourse={() => this.editCourse(course)} />
    })

    return (
      <div>
        <div>
          <div data-backdrop="static" className="modal fade" id="addCourseModal" tabIndex={-1} role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
            <div className="modal-dialog mw-100 w-75" role="document">
              <div className="modal-content">
                <div className="modal-header" ref={this.focusRef}>
                  <h5 className="modal-title" id="exampleModalLabel">Add or Remove Courses</h5>
                  <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                    <span aria-hidden="true">×</span>
                  </button>
                </div>
                <div className="modal-body">
                  <div>
                    <div className="row pl-2 border-bottom">

                      {/* Top Search Bar */}

                      <form className="form-inline" >
                        <div className="input-group mb-2 mr-sm-2">
                          <div className="input-group-prepend">

                            <div className="input-group-text">Search</div>
                          </div>
                          <input onChange={e => this.handleSearchChange(e.target.value)} type="text" className="form-control" id="inlineFormInputGroupUsername2" placeholder="Username" />
                        </div>


                        <button type="submit" className="btn btn-outline-primary mb-2">Find</button>
                      </form>



                    </div>
                    <div className="row">
                      <div className="col-md-3 border-right overflow-auto mt-4">
                        <fieldset className="form-group">

                          {/* List of all courses that have been searched for */}
                          {courseItems}

                        </fieldset>
                      </div>

                      <div className="col mt-4">

                        <div className="col-md-12 pl-3">

                          <Fade top>

                            {/* Form to create a new course */}
                            <div id="createCourseForm">
                              <AddCourseForm
                                userList={this.props.userList}
                                handleSubmit={(course) => this.saveCourse(course)} />
                            </div>

                            <div id="editCourseForm" style={{ display: "none" }}>
                              <EditCourseForm />
                            </div>

                          </Fade>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
                <div className="modal-footer">
                  <h4 className="text-success mr-5"></h4>
                  <button type="button" className="btn btn-primary" onClick={()=> this.newCourse()}>Add a New Course</button>
                  <button type="button" className="btn btn-info">Other Options</button>
                  <button type="button" className="btn btn-secondary" data-dismiss="modal" >Close</button>
                </div>
              </div>
            </div>
          </div>

        </div>
      </div>
    );
  }
}

export default AddCourseModal;
