import React, { Component } from 'react';

export default function saveUser(user) {
    if (!user.type) {
        return "User type not defined"
      }

    let path = "http://localhost:8080/"
    switch (user.type) {
        case "TEACHER":
            path="teacher/new"
            break;
        case "PARENT":
            path="parent/new"
            break;
        case "STUDENT":
            path="student/new"
            break;
        case "ADMIN":
            path="admin/new"
            break;
        default:

            break;
    }

    axios.post(path, user)
  .then((response) => {
    return response.data
  })
}
