import React, { Component } from 'react';
import Header from '../../components/main_page/header/Header';
import './admin_page.css';
import AdminLayout from './AdminLayout';
import Image from '../../images/img_avatar.png';
import axios from 'axios';

class AdminPage extends Component {
    constructor() {
        super()
        this.state = {
            user: "",
            message: ""
        }
    }

    componentDidMount() {
       
        this.retireveUserData(sessionStorage.getItem("user"))
    }

    retireveUserData(email) {
        const user = {
            email: email
        }

        axios.post("http://localhost:8080/find", user)
            .then((response) => {
                console.log(response)
                if (response.data != "") {
                    this.setState({
                        user: response.data
                    })
                } else {
                    sessionStorage.clear()
                    this.setState({
                        message: "Error retrieving data, please try again"
                    })
                }
            }
            )}

    render() {
        return (
            <div className="">
                <Header user={this.state.user} />
                <UserHomeLayout user={this.state.user} />
            </div>
        );
    }
}

export default AdminPage;
