import React, { Component, useState } from 'react';
import {
    BrowserRouter as Router,
    NavLink,
    Switch,
    Route,
    Link
} from "react-router-dom";
import LoginPage from '../../pages/login/LoginPage';
import axios from 'axios';

class LoginBox extends Component {
    constructor() {
        super()
        this.state = {
            email: "",
            password: ""
        }
    }

    setEmail(value) {
        this.setState({
            email: value
        })
    }

    setPassword(value) {
        this.setState({
            password: value
        })
    }

    handleSubmit() {
        if (this.state.email > 0 && this.state.password > 0) {
            var apiBaseUrl = "http://localhost:4000/api/";
            var self = this;
            var payload = {
                "email": this.state.username,
                "password": this.state.password
            }
            
            axios.post(apiBaseUrl + 'login', payload)
                .then(function (response) {
                    console.log(response);
                    if (response.data.code == 200) {
                        console.log("Login successfull");
                        var uploadScreen = [];
                        uploadScreen.push(<UploadScreen appContext={self.props.appContext} />)
                        self.props.appContext.setState({ loginPage: [], uploadScreen: uploadScreen })
                    }
                    else if (response.data.code == 204) {
                        console.log("Username password do not match");
                        alert("username password do not match")
                    }
                    else {
                        console.log("Username does not exists");
                        alert("Username does not exist");
                    }
                })
                .catch(function (error) {
                    console.log(error);
                });
            //search the database for the information
            //if the information matches others in the database, return and go to the correct location


        } else {

        }
    }

    render() {


        return (
            <Router>
                <div className="mt-5 mx-auto w-50 p-3">
                    <div className="rounded-lg shadow p-3 bg-light">

                        <ul className="nav nav-tabs">
                            <li className="nav-item">

                                <NavLink activeClassName="active" className="nav-link" to="/login-student">Student</NavLink>
                            </li>
                            <li className="nav-item">
                                <NavLink activeClassName="active" className="nav-link" to="/login-parent">Parent</NavLink>
                            </li>
                            <li className="nav-item">
                                <NavLink activeClassName="active" className="nav-link" to="/login-staff">Staff</NavLink>
                            </li>
                        </ul>
                        <div>
                            <form>
                                <div class="form-group mt-4">
                                    <label for="exampleInputEmail1">Email address</label>

                                    <input
                                        type="email"
                                        className="border-top-0 border-left-0 border-right-0 rounded-0 form-control"
                                        value={this.state.email}
                                        autoFocus
                                        onChange={e => this.setEmail(e.target.value)}
                                        id="email"
                                        aria-describedby="Email Address" />
                                    <small id="emailHelp" className="form-text text-muted"></small>
                                </div>
                                <div class="form-group">
                                    <label for="exampleInputPassword1">Password</label>
                                    <input
                                        type="password"
                                        className="border-top-0 border-left-0 border-right-0 rounded-0 form-control"
                                        value={this.state.password}
                                        onChange={e => this.setPassword(e.target.value)}
                                        id="password" />
                                </div>
                                <div className="form-group">
                                    <button type="submit" class="btn btn-primary w-100" onClick={this.handleSubmit}>Submit</button>
                                </div>
                                <a href="#" className="text-muted"><small>Register</small></a><a className="float-right text-muted font-size" href="#"><small>Need help?</small></a>

                            </form>
                        </div>

                    </div>
                </div>

                {/* Used to switch between user types */}

                <Switch>
                    <Route path="/login-student" />
                    <Route path="/login-parent" />
                    <Route path="/login-staff" />
                </Switch>


            </Router>
        );
    }
}

export default LoginBox;
