import React, { Component } from 'react';
import Header from '../../components/main_page/header/Header';
import './admin_page.css';
import UserHomeLayout from '../../layout/UserHomeLayout';
import axios from 'axios';
import { useDispatch } from 'react-redux';
import { getAllStudents } from '../../action/student/studentAction'

function loadAllUsers() {
    useDispatch(getAllStudents());
}

class AdminPage extends Component {
    constructor() {
        super()
        this.state = {
            user: {
                user: {
                    picture: ""
                }
            }
        }
    }


    componentWillMount() {
        
        
    }

    componentDidMount() {
        const id = sessionStorage.getItem("user")
        axios.get("http://localhost:8080/admin/find/" + id,).then((response) => {
            console.log("USER")
            console.log(response.data)
            if (response.data != "") {
                this.setState({
                    user: response.data
                })
            } else {
                sessionStorage.clear()
                this.setState({
                    message: "Error retrieving data, please logout and try again"
                })
            }
        })


    }

    render() {
        return (
            <div className="">
                <Header
                
                // updateCourseList={(courseList)=> this.updateCourseList(courseList)}
                // updateStudentList={(studentList)=> this.updateStudentList(studentList)} 
                // updateTeacherList={(teacherList)=> this.updateTeacherList(teacherList)}
                // updateParentList={(parentList)=> this.updateParentList(parentList)}
                // studentList={this.state.studentList}
                // parentList={this.state.parentList}
                // teacherList={this.state.teacherList} 
                // courseList={this.state.courseList}

                type="ADMIN"
                user={this.state.user} />
                {this.state.user ? <UserHomeLayout user={this.state.user} type="ADMIN"/> : <h3>{this.state.message}</h3>}
            </div>
        );
    }
}


export default AdminPage;
