import React, { Component } from 'react';
import axios from 'axios'

class CreateAssignmentForm extends Component {
    constructor() {
        super()
        this.state = {
            assignmentTypes: [],
            name: "",
            points: "",
            dueDate: "",
            type: "",
            selectedCourse: {}
        }
    }

    setType(value) {
        this.setState({
            type: value
        })
    }

    setName(value) {
        this.setState({
            name: value
        })
    }

    setDueDate(value) {
        this.setState({
            dueDate: value
        })
    }

    setPoints(value) {
        this.setState({
            points: value
        })
    }

    handleSubmit(event) {
        event.preventDefault()
        console.log(this.state.course)
        const assignment = {
            name: this.state.name,
            description: this.state.description,
            dueDate: this.state.dueDate,
            totalPoints: this.state.points,
            assignmentType: this.state.type,
            course: this.state.selectedCourse
        }

        axios.post("http://localhost:8080/assignment/new", assignment).then(response => {
            console.log(response.data)
        })
    }

    componentDidMount() {
        axios.get("http://localhost:8080/assignment/getTypes").then(response => {
            this.setState({
                assignmentTypes: response.data
            })
        })
    }

    render() {
        let date = "";

        // if (this.props.course) {
        //     let temp = new Date(Date.parse(this.props.user.dob))
        //     date = temp.toISOString()
        // }
        //Load the enum values of assignmentTypes above and create a mapping of them
        //May want to format the string values before inserting them
        const assignmentTypes = this.state.assignmentTypes.map(type => {
            return <option value={type}>{type}</option>
        })

        return (

            <form className="w-100 p-3" onSubmit={this.handleSubmit}>
                {/* due date, description, course */}
                <div className="row py-1">
                    <div className="col">
                        <label htmlFor="name">Name</label>
                        <input
                            onChange={e => this.setName(e.target.value)}
                            type="text"
                            value={this.state.name}
                            name="name"
                            className="form-control"
                            placeholder="Assignment Name" />
                    </div>
                    <div className="col">
                        <label htmlFor="type">Assignment Type</label>
                        <select className="custom-select" name="type">
                            {assignmentTypes}
                        </select>
                    </div>
                </div>

                <div className="row py-1">
                    <div className="col">
                        <label htmlFor="points">Points</label>
                        <input
                            onChange={e => this.setPoints(e.target.value)}
                            type="text"
                            name="points"
                            value={this.state.points}
                            className="form-control"
                            placeholder="Total Points" />
                    </div>
                    <div className="col">
                        <label htmlFor="dueDate">Due Date</label>
                        <input
                            onChange={e => this.setDueDate(e.target.value)}
                            value={date ? date.substr(0, 10) : null}
                            type="date" className="form-control"
                            id="inputAddress2"
                            name="dueDate"
                            onBlur={e => this.setDueDate(e.target.value)}
                            placeholder={date ? date.substr(0, 10) : ""} />
                    </div>
                </div>
                <div className="row py-1">
                    <div className="col">
                        <label htmlFor="description">Description</label>
                        <input
                            onChange={e => this.setDescription(e.target.value)}
                            value={this.state.description}
                            type="text"
                            className="form-control"
                            id="description"
                            name="description"
                            placeholder="Assignment Description"
                        />
                    </div>
                    <div className="col-md-2">
                    <label htmlFor="description"></label>
                        <input className="btn btn-primary mt-2" type="submit"/>
                    </div>
                </div>
            </form>
        );
    }
}

export default CreateAssignmentForm;
