import React, { Component } from 'react';
import { connect } from 'react-redux'
import '../modal.css'

import UserDetails from './UserDetails';
import ContactInformation from './ContactInformation';

class EditUserAll extends Component {
    constructor() {
        super()

        this.state = {

        }
    }


    render() {
        return (
            <div data-backdrop="static" className="modal fade modal-bg-color" id="editUser" tabIndex={-1} role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog mw-100 w-75" role="document">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title" id="exampleModalLabel">Edit User Information</h5>
                            <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                                <span aria-hidden="true">Ã—</span>
                            </button>
                        </div>

                        <div className="modal-body pt-0">

                            {/* Begin */}
                            <div>
                                <div className="row pl-5 border-bottom">

                                    {/* Top Nav Bar */}
                                <div className="btn-group my-3" role="group">
                                    <button type="button" class="btn btn-secondary px-3">User Details</button>
                                    <button type="button" class="btn btn-secondary px-3">Contact Information</button>
                                    <button type="button" class="btn btn-secondary px-3">Advanced User Edit</button>
                                </div>
                                    

                                </div>
                                <div className="row">
                                    <div className="col-md-3 border-right overflow-auto mt-4">

                                        {/* List of all users that have been searched for */}

                                    </div>
                                    <div className="col mt-4">
                                        <div className="col-md-12">
                                            <div id="userDetails">
                                                <UserDetails />
                                            </div>
                                            
                                            <div id="contactInformation">
                                                <ContactInformation />
                                            </div>

                                            <div id="otherInformation">
                                                {/* Determine user type and then display appropriate edit settings */}
                                                {this.editUserType(this.props.editUserType)}
                                            </div>

                                            {/* Form to create a new student */}


                                        </div>
                                    </div>
                                </div>
                            </div>





                        </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                            <button type="button" className="btn btn-primary">Save changes</button>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

const mapStateToProps = state => ({
    editUser: state.editUser,
    editUserType: state.editUserType
})

export default connect(mapStateToProps, {})(EditUserAll);
