import React, { Component } from 'react';
import { connect } from 'react-redux'

class CreateAssignmentTable extends Component {
    render() {

        //get course that is selected and find all assignments for that course
        const assignmentList = this.props.selectedCourse.assignments && this.props.selectedCourse.assignments.map(assignment => {
            return (
                <tr>
                    <th scope="row">{assignment.id}</th>
                    <td>{assignment.name}</td>
                    <td>{assignment.points}</td>
                    <td>{assignment.dueDate}</td>
                    <td>{assignment.type}</td>
                </tr>
            )
        })
        
        return (
            <table class="table table-hover table-sm">
                <thead>
                    <tr>
                        <th scope="col">#</th>
                        <th scope="col">Name</th>
                        <th scope="col">Points</th>
                        <th scope="col">Due Date</th>
                        <th scope="col">Type</th>
                    </tr>
                </thead>
                <tbody>
                    {assignmentList}
                </tbody>
            </table>
        );
    }
}

const mapStateToProps = state => ({
    editCourse: state.temp.editCourse
})

export default connect()(CreateAssignmentTable);
