import React, { Component } from 'react';
import axios from 'axios'

class NewsHeadLineBox extends Component {
    constructor() {
        super()
        this.getHeadline = this.getHeadline.bind(this)
        this.state = {

        }
    }

    componentDidMount() {

        this.getHeadline()
    }


    getHeadline() {
        const url = 'http://newsapi.org/v2/top-headlines?' +
            'country=us&' +
            'apiKey=3c67ffab76a84ae39504830891cbaf65';

        let data = async () => {
            const temp = axios.get(url).then(response => {
                return response.data
            })
            const temp2 = await temp

            this.setState({
                newsList: temp2
            })
        }
        data();



    }

    render() {
        console.log(this.state.newsList)
        const headLineItems = this.state.newsList && this.state.newsList.articles.map(news => {
            return (
                <div className="card" style={{width: '14rem'}}>
                    <img src={news.urlToImage} className="card-img-top" alt="..." />
                    <div className="card-body">
                        <h5 className="card-title">{news.title}</h5>
                        <p className="card-text">{news.content}</p>
                        <a href={news.url} class="btn btn-primary">Read Full Story</a>
                    </div>
                </div>
            )
        })

        return (
            <div>
                <ul className="list-unstyled">
                    {headLineItems}

                </ul>

            </div>
        );
    }
}

export default NewsHeadLineBox;
