import React, { Component } from 'react';
import axios from 'axios'

class CreateAssignmentForm extends Component {
    constructor() {
        super()
        this.state = {
            assignmentTypes: [],
            name: "",
            points: ""
        }
    }

    setName(value) {
        this.setState({
            name: value
        })
    }

    setPoints(value) {
        this.setState({
            points: value
        })
    }

    componentDidMount() {
        axios.get("http://localhost:8080/assignment/getTypes").then(response => {
            this.setState({
                assignmentTypes: response.data
            })
        })
    }

    render() {
        //Load the enum values of assignmentTypes above and create a mapping of them
        const assignmentTypes = this.state.assignmentTypes.map(type => {
            return <option value={type}>{type}</option>
        })

        return (
            
            <form className="w-100">
                {/* name, assignment type, points, due date, description, course */}
                <div className="row p-3">
                    <div className="col">
                        <label htmlFor="name">Name</label>
                        <input
                            onChange={e => this.setName(e.target.value)}
                            type="text"
                            value={this.state.name}
                            name="name"
                            className="form-control"
                            placeholder="Assignment Name" />
                    </div>
                    <div className="col">
                    <select className="custom-select my-1 mr-sm-2">
                          {assignmentTypes}
                        </select>
                </div>
                </div>

                <div className="row p-3">
                    <div className="col">
                        <input
                            onChange={e => this.setPoints(e.target.value)}
                            type="text"
                            value={this.state.points}
                            className="form-control"
                            placeholder="Total Points" />
                    </div>
                    <div className="col">
                    <select className="custom-select my-1 mr-sm-2">
                          <option selected>Choose...</option>
                          <option value={1}>One</option>
                          <option value={2}>Two</option>
                          <option value={3}>Three</option>
                        </select>
                </div>
                </div>
            </form>
        );
    }
}

export default CreateAssignmentForm;
