import React, { Component } from 'react';
import { connect } from 'react-redux'
import Trash from './../../images/icons/trash.svg'

class CreateAssignmentTable extends Component {
    render() {
        let count = 0;
        //get course that is selected and find all assignments for that course
        const assignmentList = this.props.editCourse && this.props.editCourse.assignments && this.props.editCourse.assignments.map(assignment => {
            count++;
            return (
                <tr key={assignment.id} className={this.props.editAssignment && this.props.editCourse.id === course.id ? "btn btn-primary mr-1" : "btn btn-secondary mr-1"} onClick={this.handleClick}>
                    
                    <th scope="row">{count}</th>
                    <td>{assignment.name}</td>
                    <td>{assignment.totalPoints}</td>
                    <td>{assignment.dueDate}</td>
                    <td>{assignment.assignmentType}</td>
                    {/* <td><button className="btn btn-danger"><img className="p-0" style={{width: '12px'}} src={Trash}/></button></td> */}
                    
                </tr>
            )
        })
        
        return (
            <table class="table table-hover table-sm">
                <thead>
                    <tr>
                        <th scope="col"></th>
                        <th scope="col">Name</th>
                        <th scope="col">Points</th>
                        <th scope="col">Due Date</th>
                        <th scope="col">Type</th>
                    </tr>
                </thead>
                <tbody>
                    {assignmentList}
                </tbody>
            </table>
        );
    }
}

const mapStateToProps = state => ({
    editCourse: state.temp.editCourse
})

export default connect(mapStateToProps, {})(CreateAssignmentTable);
