import { GET_ALL_STUDENTS, REMOVE_STUDENT, ADD_STUDENT, UPDATE_STUDENT } from './types';
import axios from 'axios'

export const addStudent =(user)=> dispatch => {
    axios.post("http://localhost:8080/student/new/", user)
    .then(response => {
        dispatch({
            type: ADD_STUDENT,
            payload: response.data
        })
    })
}

export const getAllStudents =()=> dispatch => {
    axios.get("http://localhost:8080/student/all")
    .then(response => {
        dispatch({
            type: GET_ALL_STUDENTS,
            payload: response.data
        })
    })
}

export const removeStudent =(id)=> dispatch => {
    console.log(id)
    axios.get("http://localhost:8080/student/delete/" + id)
    .then(response => {
        dispatch({
            type: REMOVE_STUDENT,
            payload: id
        })
    })
}

//Done
export const editStudent =(student)=> dispatch => {
    axios.post("http://localhost:8080/student/update/details", student)
    .then(response => {
        dispatch({
            type: UPDATE_STUDENT,
            payload: response.data
        })
    })
}

export const addContactStudent = (student) => dispatch => {
    axios.post("http://localhost:8080/student/add/contact", student)
    .then(response => {
        dispatch({
            type: UPDATE_STUDENT,
            payload: response.data
        })
        
    })
}

export const deleteContactStudent = (contact) => dispatch => {
    axios.post("http://localhost:8080/student/add/contact", contact)
    .then(response => {
        dispatch({
            type: UPDATE_STUDENT,
            payload: response.data
        })
        
    })
}

export const deletePhoneStudent = (contact) => dispatch => {
    axios.post("http://localhost:8080/student/add/contact", contact)
    .then(response => {
        dispatch({
            type: UPDATE_STUDENT,
            payload: response.data
        })
        
    })
}

export const addPhoneStudent = (number) => dispatch => {
    axios.post("http://localhost:8080/student/add/contact", number)
    .then(response => {
        dispatch({
            type: UPDATE_STUDENT,
            payload: response.data
        })
        
    })
}
