import { GET_USER } from '../types'

export function getUser() {
    return function(dispatch) {
        axios.get("http://localhost:8080/user/find/" + id)
    }
}


export const increment =(num)=> {
    return {
        type: 'INCREMENT',
        payload: num
    }
}

export const getCurrentUser =(id)=> {
    return {
        type: 'GET',
        payload: id
    }
}

export const setUser =(user)=> {
    return {
        type: 'SET_USER',
        payload: user
    }
}
