import React, { Component } from 'react';
import Header from '../../components/main_page/header/Header';
import './admin_page.css';
import UserHomeLayout from '../../layout/UserHomeLayout';
import axios from 'axios';

class AdminPage extends Component {
    constructor() {
        super()
        this.loadAllUsers = this.loadAllUsers.bind(this)
        this.state = {
            courseList: [],
            studentList: [],
            parentList: [],
            teacherList: [],
            user: "",
            message: ""
        }
    }

    loadAllUsers() {
        const email = sessionStorage.getItem("user")

        //Get Current User Data
        axios.post("http://localhost:8080/admin/find", email).then((response) => {
            console.log("User" + response.data)
            if (response.data != "") {
                this.setState({
                    user: response.data
                })
            } else {
                sessionStorage.clear()
                this.setState({
                    message: "Error retrieving data, please logout and try again"
                })
            }
        })

        axios.get("http://localhost:8080/course/all").then(response => {
            console.log(response.data)
            if (Array.isArray(response.data)) {
                this.setState({
                    courseList: response.data
                })
            }
        })

        axios.get("http://localhost:8080/parent/all").then(response => {
            console.log(response.data)
            if (Array.isArray(response.data)) {
                this.setState({
                    studentList: response.data
                })
            }
        })

        axios.get("http://localhost:8080/student/all").then(response => {
            console.log(response.data)
            if (Array.isArray(response.data)) {
                this.setState({
                    studentList: response.data
                })
            }
        })

        axios.get("http://localhost:8080/teacher/all").then(response => {
            console.log(response.data)
            if (Array.isArray(response.data)) {
                this.setState({
                    teacherList: response.data
                })
            }
        })
    }

    // updateTeacherList(tempTeacherList) {
    //     this.setState({
    //         teacherList: tempTeacherList
    //     })
    // }

    // updateStudentList(tempStudentList) {
    //     this.setState({
    //         studentLst: tempStudentList
    //     })
    // }

    // updateCourseList(tempCourseList) {
    //     this.setState({
    //         courseList: tempCourseList
    //     })
    // }

    // updateParentList(tempParentList) {
    //     this.setState({
    //         parentList: tempParentList
    //     })
    // }

    componentWillMount() {
        
        
    }

    componentDidMount() {
        this.loadAllUsers()
    }

    render() {
        return (
            <div className="">
                <Header
                
                // updateCourseList={(courseList)=> this.updateCourseList(courseList)}
                // updateStudentList={(studentList)=> this.updateStudentList(studentList)} 
                // updateTeacherList={(teacherList)=> this.updateTeacherList(teacherList)}
                // updateParentList={(parentList)=> this.updateParentList(parentList)}
                // studentList={this.state.studentList}
                // parentList={this.state.parentList}
                // teacherList={this.state.teacherList} 
                // courseList={this.state.courseList}

                user={this.state.user} />
                {this.state.user ? <UserHomeLayout userList={this.state.userList} user={this.state.user} /> : <h3>{this.state.message}</h3>}
            </div>
        );
    }
}


export default AdminPage;
