import React, { Component } from 'react';
import './modal.css'
import Fade from 'react-reveal/Fade'
import Slide from 'react-reveal/Slide'
import axios from 'axios'

class EditUser extends Component {
    constructor(props) {

        super()
        //this.handleSubmit = this.handleSubmit.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this)
        this.state = {
            newUser: "",
            dob: "",
            firstName: "",
            lastName: "",
            email: "",
            password: "",
            address: "",
            unit: "",
            city: "",
            state: "",
            zipcode: "",
            style: {
                display: "none"
            },
            currentAddress: "",
            useAddress: false
        }
    }

    handleSubmit(event) {
        event.preventDefault();

        const user = {
            id: this.props.user.id,
            type: this.props.user.type,
            firstName: this.state.firstName ?  this.state.firstName : event.target.firstName.placeholder ,
            lastName: this.state.lastName ? this.state.lastName : event.target.lastName.placeholder,
            //dob: this.state.dob ? event.target.firstName.placeholder : this.state.firstName,
            email: this.state.email ? this.state.email : event.target.email.placeholder,
            password: this.state.password ? this.state.password : event.target.password.placeholder,
            contactInformation: this.state.useAddress ? ([{
                address: this.state.address ? event.target.address.placeholder : this.state.address,
                city: this.state.city ? event.target.city.placeholder : this.state.city,
                state: this.state.state ? event.target.state.placeholder : this.state.state,
                zipcode: this.state.zipcode ? event.target.zipcode.placeholder : this.state.zipcode
            }]) : null
        }

        if (!user.type) {
            return "User type not defined"
          }
          let path = ""
          switch (user.type) {
            case "TEACHER":
              path = "teacher/update"
              break;
            case "PARENT":
              path = "parent/update"
              break;
            case "STUDENT":
              path = "student/update"
              break;
            case "ADMIN":
              path = "admin/update"
              break;
            default:
      
              break;
          }

        const currentPath = "http://localhost:8080/" + path

        axios.post(currentPath, user).then(response => {
            this.props.newUser(response.data)
            this.setState({
                newUser: response.data
            })
         })

    }



setDob(value) {
    this.setState({
        dob: value
    })
}

setEmail(value) {

    this.setState({
        email: value
    })
}

setPassword(value) {
    this.setState({
        password: value
    })
}

setFirstName(value) {
    this.setState({
        firstName: value
    })
}

setLastName(value) {
    this.setState({
        lastName: value
    })
}

setAddress(value) {
    this.setState({
        address: value

    })
}

setUnit(value) {
    this.setState({

        unit: value

    })
}

setCity(value) {
    this.setState({

        city: value

    })
}

setUserState(value) {
    this.setState({

        state: value

    })
}

setZipcode(value) {
    this.setState({

        zipcode: value

    })
}

setType(value) {

    this.setState({
        type: value
    })
}

handleAddress(value) {
    this.setState({
        currentAddress: value
    })
}

showAddress() {
    const status = document.getElementById("addressEdit").style.display
    if (status == "block") {
        document.getElementById("addressEdit").style.display = "none"
        this.setState({
            useAddress: false
        })
    } else {
        document.getElementById("addressEdit").style.display = "block";
        this.setState({
            useAddress: true
        })
    }
}


componentDidMount(prevProps) {
    let date ="";
    
    if(this.props.user.dob) {
        let temp = new Date(Date.parse(this.props.user.dob))
        console.log(temp)
        date = temp.toISOString()
    }

    if (this.props.user.dob !== prevProps.user.dob) {
        this.setState({
          dob: date
        })
      }
}

componentDidUpdate(prevProps) {

}

render() {
    let date ="";

    const contactInformation = function (props) {
        return this.props.user.contactInformation
    }

    const Addresses = this.props.user.contactInformation && this.props.user.contactInformation.map(item => {
        return <option key={item}>{item.address} {item.unit} {item.city}, {item.state} {item.zipcode}</option>
    })
    
    if(this.props.user.dob) {
        let temp = new Date(Date.parse(this.props.user.dob))
        console.log(temp)
        date = temp.toISOString()
    }


    return (
        <div>

            <div data-backdrop="static" className="modal fade modal-bg-color" id="editUser" tabIndex={-1} role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog mw-75 w-75" role="document">
                    <div className="modal-content">
                        <div className="modal-header" ref={this.focusRef}>
                            <h5 className="modal-title" id="exampleModalLabel">Add or Remove Users</h5>
                            <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                                <span aria-hidden="true">Ã—</span>
                            </button>
                        </div>
                        <div className="modal-body p-5">
                            <form onSubmit={this.handleSubmit} id="form-to-clear">
                                <div className="form-row w-100">
                                    <div className="form-group col-md-6 w-100">
                                        <label htmlFor="email">Email address</label>
                                        <input
                                            onChange={e => this.setEmail(e.target.value)}
                                            type="email"
                                            name="email"
                                            className="form-control w-100"
                                            id="email"
                                            aria-describedby="emailHelp"
                                            value={this.state.email ? this.state.email : null}
                                            placeholder={this.props.user.email ? this.props.user.email : "Enter email"}
                                        />

                                    </div>
                                    <div className="form-group col-md-6 w-100">
                                        <label htmlFor="password">Password</label>
                                        <input
                                            onChange={e => this.setPassword(e.target.value)}
                                            type="password" name="password"
                                            className="form-control w-100"
                                            id="password"
                                            value={this.state.password ? this.state.password : null}
                                            placeholder={this.props.user.password ? this.props.user.password : "Password"}
                                        />

                                    </div>
                                </div>

                                <div className="form-row w-100">

                                    <div className="form-group col-md-6 w-100">
                                        <label htmlFor="password">First Name</label>
                                        <input
                                            value={this.state.firstName ? this.state.firstName : null}
                                            onChange={e => this.setFirstName(e.target.value)}
                                            type="text"
                                            name="firstName"
                                            className="form-control w-100"
                                            id="firstName"
                                            aria-describedby="emailHelp"
                                            placeholder={this.props.user.firstName ? this.props.user.firstName : "First Name"}
                                        />
                                    </div>

                                    <div className="form-group col-md-6 w-100">
                                        <label htmlFor="password">Last Name</label>
                                        <input
                                            value={this.state.lastName ? this.state.lastName : null}
                                            onChange={e => this.setLastName(e.target.value)}
                                            type="text" name="lastName"
                                            className="form-control w-100"
                                            id="lastName"
                                            placeholder={this.props.user.lastName ? this.props.user.lastName : "Last Name"} />
                                    </div>
                                </div>

                                {/* Dates not working correctly yet */}
                                <div className="form-group col">
                                    <label for="inputAddress2">Date of Birth</label>
                                    {console.log(date.substr(0,10))}
                                    <input
                                        onChange={e => this.setDob(e.target.value)}
                                        value={this.state.dob ? this.state.dob : null}                 
                                        type="date" className="form-control"
                                        onfocus="(this.type='date')"
                                        onblur="(this.type='text')"
                                        id="inputAddress2"
                                        name="dob"
                                        placeholder={date ? date.substr(0,10) : ""} />

                                </div>

                                <div class="input-group col">
                                    <div class="input-group-prepend">
                                        <button class="btn btn-outline-primary" type="button" onClick={this.showAddress}>Edit</button>
                                    </div>
                                    <select id="addressPick" onChange={e => this.handleAddress(e.target.key)}
                                        className="custom-select form-control"
                                        name="type" required>
                                        {Addresses}
                                    </select>
                                </div>

                                <Fade top cascade>
                                    <div id="addressEdit" className="mt-3" style={this.state.style}>
                                        <div className="form-group">
                                            <label for="inputAddress">Address</label>
                                            <input
                                                onChange={e => this.setAddress(e.target.value)}
                                                type="text" className="form-control" id="inputAddress"
                                                name="address"
                                                value={this.state.address ? this.state.address : null}
                                                placeholder={contactInformation.address ? null : "1234 Main Street"} />
                                        </div>

                                        <div className="form-group">
                                            <label for="inputAddress2">Address 2</label>
                                            <input
                                                onChange={e => this.setUnit(e.target.value)}
                                                type="text" className="form-control" id="inputAddress2"
                                                name="unit"
                                                value={this.state.unit ? this.state.unit : null}
                                                placeholder={this.state.currentAddress.unit ? this.state.currentAddress.unit : "Unit or Apartment Number"} />
                                        </div>


                                        <div className="form-row">
                                            <div className="form-group col-md-6">
                                                <label for="inputCity">City</label>
                                                <input
                                                    onChange={e => this.setCity(e.target.value)}
                                                    type="text" className="form-control" name="city"
                                                    id="inputCity"
                                                    value={this.state.city ? this.state.city : null}
                                                    placeholder={this.state.currentAddress.city ? this.state.currentAddress.city : "City"} />
                                            </div>
                                            <div className="form-group col-md-4">
                                                <label for="inputState">State</label>
                                                <select onChange={e => this.setUserState(e.target.value)} id="inputState" name="state" className="form-control">
                                                    <option selected>{this.state.currentAddress.state}</option>
                                                    {this.state.states}
                                                </select>
                                            </div>
                                            <div className="form-group col-md-2">
                                                <label for="inputZip">Zip</label>
                                                <input onChange={e => this.setZipcode(e.target.value)}
                                                    type="text" name="zipcode"
                                                    className="form-control"
                                                    id="inputZip"
                                                    value={this.state.zipcode ? this.state.zipcode : null}
                                                    placeholder={this.state.currentAddress.zipcode ? this.state.currentAddress.zipcode : "55555"} />
                                            </div>
                                        </div>
                                    </div>
                                </Fade>


                                <div className="form-row">
                                    <div className="col">
                                        <input type="submit" className="btn btn-primary w-100 mt-4">
                                        </input>
                                    </div>
                                </div>
                            </form>
                        </div>
                        <div className="modal-footer">
                            <h4 className="text-success mr-5">{this.state.success}</h4>
                            <button type="button" className="btn btn-secondary" data-dismiss="modal" >Close</button>
                        </div></div>
                </div>
            </div>

        </div>

    );
}
}

export default EditUser;
