import React, { Component } from 'react';
//import Calendar from 'react-calendar';
//import 'react-calendar/dist/Calendar.css';
import { connect } from 'react-redux'

class CalendarInsert extends Component {
  constructor() {
    super()
    this.onChange = this.onChange.bind(this)
    this.handleAdd = this.handleAdd.bind(this)
    this.state = {
      calendarItems: []
    }
  }

  handleAdd() {

    const newItem = {
      date: this.state.date,
      description: this.state.description
    }

    

    let tempList = this.state.calendarItems
    tempList.push(newItem)
    this.setState({
      date: '',
      description: '',
      calendarItems: tempList
    })
  }

  onChange(e) {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  render() {

      const calendarTable = this.state.calendarItems.map(item => {
        return (
          <tr>
            <td>{item.date}</td>
            <td>{item.description}</td>
          </tr>
        )
      })


    return (
      <div>
        <input type="date" className="form-control my-2" name="date" onChange={this.onChange}></input>
        <div className="input-group mb-3">
          <input type="text" class="form-control" name="description" onChange={this.onChange} placeholder="Description" />
          <div class="input-group-append">
            <button class="btn btn-outline-secondary" type="button" onClick={this.handleAdd}>Add</button>
          </div>
        </div>

        <table class="table table-borderless table-hover">
          <thead>
            <tr>
              <th scope="col">Date</th>
              <th scope="col">Description</th>
            </tr>
          </thead>
          <tbody>
            {calendarTable}
          </tbody>
        </table>


      </div>
    );
  }
}

const mapStateToProps = state => ({
  calendarItems: state.user.user.calendarList
})

export default connect(mapStateToProps, {})(CalendarInsert)
