import React, { Component } from 'react';
import axios from 'axios'
import UserListing from './UserListing';
import AddUserForm from './AddUserForm';
import teacherReducer from './../../../../reducer/teacher/teacherReducer';
import studentReducer from './../../../../reducer/student/studentReducer';
import { useSelector, connect } from 'react-redux';
import adminReducer from './../../../../reducer/admin/adminReducer';
import store from './../../../../store';
import { PropTypes } from 'prop-types';

class AddUserModal extends Component {
  constructor() {
    super()

    this.onChange = this.onChange.bind(this)
    this.state = {
      search: ""
    }
  }
  
  onChange(e) {
    this.setState({[e.target.name]: e.target.value})
  }

  componentDidMount() {
    
  }

  checkUser(firstName, lastName) {
    const length = this.state.search.length
    if (this.state.search == firstName.substr(0, length) || this.state.search == lastName.substr(0, length)) {
      return true
    }
  }

  render() {
    const teacherList = this.props.teachers.teachers
    const parentList = this.props.parents.parents
    const studentList = this.props.students.students

    const studentComponentList = studentList && studentList.filter(student => {
      const firstName = student.user.firstName
      const lastName = student.user.lastName
      const fullName = firstName + " " + lastName
      if (this.checkUser(firstName, lastName)) {
        return <UserListing key={student.id} text={fullName} />
      }
    })

    const teacherComponentList = teacherList && teacherList.map(teacher => {
      const firstName = teacher.user.firstName 
      const lastName = teacher.user.lastName
      const fullName = firstName + " " + lastName
      if (this.checkUser(firstName, lastName)) {
        return <UserListing key={teacher.id} text={fullName} />
      } else {
        
      }
    })

    const parentComponentList = parentList && parentList.map(parent => {
      const firstName =  parent.user.firstName;
      const lastName = parent.user.lastName;
      const fullName = firstName + " " + lastName
      if (this.checkUser(firstName, lastName)) {
        return <UserListing key={parent.id} text={fullName} />
      }
    })

    return (
      <div>
        <div data-backdrop="static" className="modal fade" id="addStudentModal" tabIndex={-1} role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
          <div className="modal-dialog mw-100 w-75" role="document">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title" id="exampleModalLabel">Add or Remove Users</h5>
                <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                  <span aria-hidden="true">Ã—</span>
                </button>
              </div>
              <div className="modal-body">
                <div>
                  <div className="row pl-2 border-bottom">

                    {/* Top Search Bar */}

                    <form className="form-inline" >
                      <div className="input-group mb-2 mr-sm-2">
                        <div className="input-group-prepend">

                          <div className="input-group-text">Search</div>
                        </div>
                        <input onChange={this.onChange} 
                        value={this.state.search} type="text" name="search"
                        className="form-control" placeholder="Username" />
                      </div>

                      {/* Select user dropdown menu */}
                      <div className="input-group mb-2 mr-sm-2">
                        <select className="custom-select my-1 mr-sm-2" name="type" onChange={this.onChange} value={this.state.type}>
                          <option selected>Choose...</option>
                          <option value={"student"}>Student</option>
                          <option value={"admin"}>Admin</option>
                          <option value={"parent"}>Parent</option>
                          <option value={"teacher"}>Teacher</option>
                        </select>
                      </div>

                      <button type="submit" className="btn btn-outline-primary mb-2">Find</button>
                    </form>

                  </div>
                  <div className="row">
                    <div className="col-md-3 border-right overflow-auto mt-4">

                      {/* List of all users that have been searched for */}
                      <h6>Students</h6>
                      <div>
                      {studentComponentList && studentComponentList.length > 0 ? studentComponentList : <small>No Results</small>}
                      </div>
                      
                      <h6>Parents</h6>
                      {parentComponentList && parentComponentList.length ? parentComponentList : <small>NoResults</small>}
                      <h6>Teachers</h6>
                      {teacherComponentList ? teacherComponentList : <small>No Results</small>}
                    </div>

                    <div className="col mt-4">
                      <div className="col-md-12">

                        {/* Form to create a new student */}
                        <AddUserForm />

                      </div>
                    </div>
                  </div>
                </div>
              </div>
              <div className="modal-footer">
                {/* <h4 className="text-success mr-5">{this.state.success}</h4> */}
                <button type="button" className="btn btn-secondary" data-dismiss="modal" >Close</button>
              </div>
            </div>
          </div>
        </div>

      </div>
    );
  }
}


const mapStateToProps = state => ({
  user: state.user,
  students: state.students,
  parents: state.parents,
  teachers: state.teachers
})

export default connect(mapStateToProps, {})(AddUserModal);
