import React, { useState } from 'react';
import axios from 'axios'
import UserListing from './UserListing';
import AddUserForm from './AddUserForm';
import teacherReducer from './../../../../reducer/teacher/teacherReducer';
import studentReducer from './../../../../reducer/student/studentReducer';
import { useSelector } from 'react-redux';
import adminReducer from './../../../../reducer/admin/adminReducer';
import State from '../../../../store'

function AddUserModal() {

    const [search, setSearch] = useState("");
    const [type, setType] = useState("");

    //const userList = useSelector(state => state.userListReducer)
    const teacherList = useSelector(state => state.teacherReducer);
    const studentList = useSelector((state: State) => state.studentReducer);
    const adminList = useSelector(state => state.adminReducer);
    const parentList = useSelector(state => state.parentReducer);


    console.log(studentList)
    const studentComponentList = studentList && studentList.map(student => {
      const fullName = student.user.firstName + " " + student.user.lastName
      const firstName = student.user.firstName
      const lastName = student.user.lastName
      const length = search.length
      if (search === firstName.substr(0,length)) {
        console.log(student)
        return <UserListing text={fullName}/>
      }
    })



    return (
      <div>
        <div data-backdrop="static" className="modal fade" id="addStudentModal" tabIndex={-1} role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
          <div className="modal-dialog mw-100 w-75" role="document">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title" id="exampleModalLabel">Add or Remove Users</h5>
                <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                  <span aria-hidden="true">×</span>
                </button>
              </div>
              <div className="modal-body">
                <div>
                  <div className="row pl-2 border-bottom">

                    {/* Top Search Bar */}

                    <form className="form-inline" >
                      <div className="input-group mb-2 mr-sm-2">
                        <div className="input-group-prepend">

                          <div className="input-group-text">Search</div>
                        </div>
                        <input onChange={e => setSearch(e.target.value)} type="text" className="form-control" id="inlineFormInputGroupUsername2" placeholder="Username" />
                      </div>
                      {/* Select user dropdown menu */}
                      <div className="input-group mb-2 mr-sm-2">
                        <select onChange={e => setType(e.target.value)} className="custom-select my-1 mr-sm-2">
                          <option selected>Choose...</option>
                          <option value={"student"}>Student</option>
                          <option value={"admin"}>Admin</option>
                          <option value={"parent"}>Parent</option>
                          <option value={"teacher"}>Teacher</option>
                        </select>
                      </div>

                      <button type="submit" className="btn btn-outline-primary mb-2">Find</button>
                    </form>



                  </div>
                  <div className="row">
                    <div className="col-md-3 border-right overflow-auto mt-4">

                      {/* List of all users that have been searched for */}
                      <h6>Students</h6>
                      {studentComponentList}
                      <h6>Parents</h6>
                     
                      <h6>Teachers</h6>
                      
                    </div>

                    <div className="col mt-4">
                      <div className="col-md-12">

                        {/* Form to create a new student */}
                        <AddUserForm handleSubmit={(user) => this.saveUser(user)} />

                      </div>
                    </div>
                  </div>
                </div>
              </div>
              <div className="modal-footer">
                {/* <h4 className="text-success mr-5">{this.state.success}</h4> */}
                <button type="button" className="btn btn-secondary" data-dismiss="modal" >Close</button>
              </div>
            </div>
          </div>
        </div>

      </div>
    );
  
}

export default AddUserModal;
