import React, { Component } from 'react';
import axios from 'axios'

class EditCourseForm extends Component {
    constructor() {
        super()
        this.handleSubmit = this.handleSubmit.bind(this)
        this.state = {
            name: "",
            startDate: "",
            endDate: "",
            startTime: "",
            roomNumber: "",
            courseMeeting: "",
            teacher: ""
        }
    }

    setName(value) {
        this.setState({
            name: value
        })
    }

    setTeacher(value) {
        this.setState({
            teacher: value
        })
    }

    setStartDate(value) {
        this.setState({
            startDate: value,
        })
    }

    setEndDate(value) {
        this.setState({
            endDate: value
        })
    }

    setStartTime(value) {
        this.setState({
            startTime: value
        })
    }

    setRoomNumber(value) {
        this.setState({
            roomNumber: value
        })
    }

    setCourseMeeting(value) {
        let currentMeeting = this.state.courseMeeting
        currentMeeting = currentMeeting + "-" + value
        this.setState({
            courseMeeting: currentMeeting
        })
    }

    handleSubmit(event) {
        event.preventDefault();
        console.log(this.state)
        const course = {
            name: this.state.name ? this.state.name : event.target.name.placeholder,
            teacher: this.state.teacher ? this.state.teacher : event.target.teacher.placeholder,
            startDate: this.state.startDate ? this.state.startDate : event.target.startDate.placeholder,
            endDate: this.state.endDate ? this.state.endDate : event.target.endDate.placeholder,
            time: this.state.startTime ? this.state.startTime : event.target.startTime.placeholder,
            room: this.state.roomNumber ? this.state.roomNumber : event.target.roomNumber.placeholder,
            meetsOn: this.state.courseMeeting
        }

        axios.post("http://localhost:8080/course/update", course).then(response => {
            this.props.changeCourse(response.data)
        })
    }

    componentDidUpdate() {
        const course = this.props.course
        try {
            this.setState({
                name: course.name ? course.name : "Course Name",
                teacher: course.teacher ? course.teacher : null,
                startDate: course.startDate ? course.startDate : null,
                endDate: course.endDate,
                time: course.time,
                room: course.room,
                meetsOn: course.meetsOn
    
            })
        } catch (error) {
            
        }
    }

    render() {
        let startDate = "";
        let endDate = ""
        if (this.props.course !== this.state) {
            //this.componentWillReceiveProps()
        }

        if (this.props.course.startDate) {
            let temp = new Date(Date.parse(this.props.course.startDate))
            startDate = temp.toISOString()
        }

        if (this.props.course.endDate) {
            let temp = new Date(Date.parse(this.props.course.endDate))
            endDate = temp.toISOString()
        }

        const teacherList = this.props.userList.map(user => {
            if (user.type === "TEACHER") {
                try {
                    if (user.id == this.props.course.teacher.id) {
                        return <option selected value={user.id}>{user.firstName} {user.lastName}</option>
                    }
                } catch (error) {

                }
                return <option value={user.id}>{user.firstName} {user.lastName}</option>
            }
        })
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <div className="row">
                        <div className="col">
                            <div className="form-group col">
                                <label for="inputAddress2">Course Start Date</label>
                                <input
                                    onChange={e => this.setStartDate(e.target.value)}
                                    //value={startDate ? startDate.substr(0, 10) : null}
                                    type="date" className="form-control"
                                    id="inputAddress2"
                                    name="dob"
                                    placeholder={startDate ? startDate.substr(0, 10) : ""} />

                            </div>
                        </div>
                        <div className="col">
                            <div className="form-group col">
                                <label for="inputAddress2">Course End Date</label>
                                <input
                                    onChange={e => this.setEndDate(e.target.value)}
                                    //value={endDate ? endDate.substr(0, 10) : null}
                                    type="date" className="form-control"
                                    //onfocus={e=> this.setDob(e.target.selected)}
                                    id="inputAddress2"
                                    name="dob"
                                    placeholder={endDate ? endDate.substr(0, 10) : ""} />

                            </div>
                        </div>

                    </div>
                    <div className="row">
                        <div className="col">
                            <div className="form-group col">
                                <label for="inputAddress2">Start Time</label>
                                <input
                                    onChange={e => this.setStartTime(e.target.value)}
                                    //value={this.state.startTime}
                                    type="time" className="form-control"
                                    //onfocus={e=> this.setDob(e.target.selected)}
                                    id="inputAddress2"
                                    name="dob"
                                    placeholder={this.state.startTime} />

                            </div>
                        </div>
                        <div className="col">
                            <div className="form-group col">
                                <label for="inputAddress2">Room Number</label>
                                {/* Turn this into a series of checkboxes */}
                                <input
                                    onChange={e => this.setRoomNumber(e.target.value)}
                                    //value={this.state.roomNumber}
                                    type="text" className="form-control"
                                    id="inputAddress2"
                                    name="courseMeeting"
                                    placeholder={this.state.roomNumber} />

                            </div>
                        </div>
                    </div>
                    <div className="row">
                        <div className="form-group col w-100">
                            <label htmlFor="teacher">Teacher</label>
                            <select className="custom-select mr-sm-2" id="teacher-id"
                                onChange={e => this.setTeacher(e.target.value)} name="teacher">
                                {teacherList}
                            </select>

                        </div>
                        <div className="form-group col">
                            <label for="inputAddress2">Class Name</label>
                            {/* Turn this into a series of checkboxes */}
                            <input
                                onChange={e => this.setName(e.target.value)}
                                //value={this.state.name}
                                type="text" className="form-control"
                                id="inputAddress2"
                                name="courseMeeting"
                                placeholder={this.state.name}
                                 />

                        </div>

                    </div>


                    <div className="row">
                        <div className="col">
                            <h6 className="mb-3">Class Meet Days:</h6>
                            <div className="form-group row">

                                <div className="form-check form-check-inline">
                                    <input onClick={e => this.setCourseMeeting(e.target.value)}
                                        value={"M"} type="checkbox" className="form-check-input" id="M" name="courseMeeting" />
                                    <label className="form-check-label" for="M">M</label>
                                </div>
                                <div className="form-check form-check-inline">
                                    <input onClick={e => this.setCourseMeeting(e.target.value)}
                                        value={"T"} type="checkbox" className="form-check-input" id="T" name="courseMeeting" />
                                    <label className="form-check-label" for="T">T</label>
                                </div>
                                <div className="form-check form-check-inline">
                                    <input onClick={e => this.setCourseMeeting(e.target.value)}
                                        value={"W"} type="checkbox" className="form-check-input" id="W" name="courseMeeting" />
                                    <label className="form-check-label" for="W">W</label>
                                </div>
                                <div className="form-check form-check-inline">
                                    <input onClick={e => this.setCourseMeeting(e.target.value)}
                                        value={"Th"} type="checkbox" className="form-check-input" id="Th" name="courseMeeting" />
                                    <label className="form-check-label" for="Th">Th</label>
                                </div>
                                <div className="form-check form-check-inline">
                                    <input onClick={e => this.setCourseMeeting(e.target.value)}
                                        value={"F"} type="checkbox" className="form-check-input" id="F" name="courseMeeting" />
                                    <label className="form-check-label" for="F">F</label>
                                </div>

                            </div>
                        </div>


                    </div>
                    <button type="submit" className="btn btn-primary w-100">Add Class Details</button>



                </form>
            </div>
        );
    }
}

