import React, { useState, useEffect } from 'react';
import Header from '../../components/main_page/header/Header';
import './admin_page.css';
import UserHomeLayout from '../../layout/UserHomeLayout';
import axios from 'axios';
import { useDispatch } from 'react-redux';
import { getAllStudents } from '../../action/student/studentAction'



function AdminPage() {

    const [user, setUser] = useState({})
    const [message, setMessage] = useState("")

    useEffect(() => {
        const id = sessionStorage.getItem("user")
        axios.get("http://localhost:8080/admin/find/" + id,).then((response) => {
            console.log("USER")
            console.log(response.data)
            if (response.data != "") {
                setUser()
            } else {
                sessionStorage.clear()
                this.setState({
                    message: "Error retrieving data, please logout and try again"
                })
            }
        })


    })


    return (
        <div className="">
            <Header
                type="ADMIN"
                user={user} />
            {this.state.user ? <UserHomeLayout user={user} type="ADMIN" /> : <h3>{this.state.message}</h3>}
        </div>
    );

}


export default AdminPage;
