import React, { Component } from 'react';

class AssignStudentForm extends Component {
    render() {

        const studentList = this.props.selectedStudents.map(student => {
            return <p>{student.firstName} {student.lastName}</p>
        })

        const length = this.props.course.name ? " - " + studentList.length : null

        return (
            <div>
                <h4>{this.props.course.name} {length}</h4>
                {studentList}
            </div>
        );
    }
}

const mapStateToProps = state => ({
    studentList: state.temp.studentList
})

export default connect()(AssignStudentForm);
