import React, { Component } from 'react';
import LoginBoxHeader from './LoginBoxHeader';

class LoginBox extends Component {
    constructor() {
        super()
        this.toggleClick = this.toggleClick.bind(this)
        this.state = {
            active: false
        };
    }

    toggleClick() {
        const currentState = this.state.active;
        this.setState(
            {active: !currentState}
        )
    }

    render() {
        const activeLink = "nav-link active";
        const nonActiveLink = "nav-link";
        const currentStyle = "nav-link";


        return (
            <div className="mt-5 mx-auto w-50 p-3">
                <div className="rounded-lg shadow p-3 bg-light">
       
                    <ul className="nav nav-tabs">
                        <li className="nav-item">
                            <a className={this.state.active ? "nav-link active" : {nonActiveLink}} onClick={this.toggleClick} href="#">Student</a>
                        </li>
                        <li className="nav-item">
                            <a className={this.state.active ? {activeLink} : {nonActiveLink}} onClick={this.toggleClick} href="#">Parent</a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link" href="#">Staff</a>
                        </li>
                    </ul>
                    <div>
                        <form>
                            <div class="form-group mt-4">
                                <label for="exampleInputEmail1">Email address</label> 
                                
                                <input type="email" 
                                    className="border-top-0 border-left-0 border-right-0 rounded-0 form-control" 
                                    id="exampleInputEmail1" 
                                    aria-describedby="emailHelp" />
                                <small id="emailHelp" className="form-text text-muted"></small>
                            </div>
                            <div class="form-group">
                                <label for="exampleInputPassword1">Password</label>
                                <input type="password" className="border-top-0 border-left-0 border-right-0 rounded-0 form-control" id="exampleInputPassword1" />
                            </div>
                            <div className="form-group">
                                <button type="submit" class="btn btn-primary w-100">Submit</button>
                            </div>
                            <a href="#"className="text-muted"><small>Register</small></a><a className="float-right text-muted font-size" href="#"><small>Need help?</small></a>
                            
                        </form>
                    </div>

                </div>
            </div>
        );
    }
}

export default LoginBox;
