import { GET_ALL_STUDENTS } from './../types';

export const addStudent =(student)=> {
    return {
        type: 'ADD',
        payload: student
    }
}

export const getAllStudents =(student)=> {
    return {
        type: GET_ALL_STUDENTS,
        payload: student
    }
}
