import React, { Component } from 'react';
import { HotTable } from '@handsontable/react'
import 'handsontable/dist/handsontable.full.css';
import { connect } from 'react-redux';

class Table extends Component {
    constructor(props) {
        super(props)
        this.data = [
            ["", "Ford", "Volvo", "Toyota", "Honda"], //headers is a function
            ["2016", 10, 11, 12, 13],
            ["2017", 20, 11, 14, 13],
            ["2018", 30, 15, 12, 13]
        ];
    }

    // getSchema() {
    //     return (
    //         {}
    //     )
    // }

    getHeaders() {
        let headers = this.props.editCourse.assignments.map(assignment => {
            return assignment.name
        })
        headers.unshift("")
        return (
            headers
        )
    }

    render() {
        const headers = ["FART", "FARTssss", "DOUBLE Farts"]
        return (
            <div id="hot-app">
                <HotTable licenseKey={'non-commercial-and-evaluation'} data={this.data} colHeaders={this.getHeaders} rowHeaders={true} width="600" height="300" />
            </div>
        );
    }
}

const mapStateToProps = state => ({
    editCourse: state.temp.editCourse,
    temp: state.temp.grid,
  })
  
  export default connect(mapStateToProps, {})(Table)
