//     });
// }

async function AdminPage() {

    useDispatch(getAllStudents());

    const [user, setUser] = useState({});
    const [message, setMessage] = useState("");
    const setUserMethod = (data) => setUser(data);
    const setMessageMethod = (data) => setMessage(data);

    console.log(sessionStorage.getItem("user"));
    const id = sessionStorage.getItem("user");
    getUser(id);
    const response = await axios.get("http://localhost:8080/admin/find/" + id,);
    
    response.data ? setUser(response.data) : setMessage(response.data);


    console.log(user);
    return (
        <div className="">
            <Header
                type="ADMIN"
                user={user} />
            {user ? <UserHomeLayout user={user} type="ADMIN" /> : <h3>{message}</h3>}
        </div>
    );

}


export default AdminPage;
