import axios from 'axios';
//Reducer is a switch statement with actions and defines those actions

//state is the initial state 
const initialState = {}

//Is it better to return the list from the API call and set state to that, or update state on my own
const studentReducer = (state = getStudentList(), action) => {
    switch (action.type) {
        case 'ADD':
            return state;
        case 'REMOVE':
            return null;
        case 'UPDATE':
            return null;
        case "ALL":
            return state;
        default:
            return state;
    }
} 

export default studentReducer;
