import React, { Component } from 'react';
import axios from 'axios'
import { connect } from 'react-redux'

class EditCourseForm extends Component {
    constructor() {
        super()
        this.handleSubmit = this.handleSubmit.bind(this)
        this.state = {
            name: "",
            startDate: "",
            endDate: "",
            startTime: "",
            roomNumber: "",
            courseMeeting: "",
            teacher: ""
        }
    }

    setName(value) {
        this.setState({
            name: value
        })
    }

    setTeacher(value) {
        this.setState({
            teacher: value
        })
    }

    setStartDate(value) {
        console.log(value)
        this.setState({
            startDate: value,
        })
        console.log(this.state.startDate)
    }

    setEndDate(value) {
        this.setState({
            endDate: value
        })
    }

    setStartTime(value) {
        this.setState({
            startTime: value
        })
    }

    setRoomNumber(value) {
        this.setState({
            roomNumber: value
        })
    }

    setCourseMeeting(value) {
        let currentMeeting = this.state.courseMeeting
        currentMeeting = currentMeeting + "-" + value
        this.setState({
            courseMeeting: currentMeeting
        })
    }

    handleSubmit(event) {
        event.preventDefault();
        
        const course = {
            id: this.props.course.id,
            name: this.state.name ? this.state.name : this.props.course.name,
            teacher: this.state.teacher ? this.state.teacher : this.props.course.teacher,
            startDate: this.state.startDate ? this.state.startDate : this.props.course.startDate,
            endDate: this.state.endDate ? this.state.endDate : this.props.course.endDate,
            time: this.state.startTime ? this.state.startTime : this.props.course.time,
            room: this.state.roomNumber ? this.state.roomNumber : this.props.course.room,
            meetsOn: this.state.courseMeeting
        }
        console.log(course)
        axios.post("http://localhost:8080/course/update", course).then(response => {
            this.props.changeCourse(response.data)
        })
    }

    render() {
        let startDate = "";
        let endDate = ""
        console.log(this.props.editCourse)
        if (this.props.editCourse && this.props.editCourse.startDate) {
            let temp = new Date(Date.parse(this.props.editCourse.startDate))
            startDate = temp.toISOString()
        }

        // if (this.props.course.endDate) {
        //     let temp = new Date(Date.parse(this.props.course.endDate))
        //     endDate = temp.toISOString()
        // }


        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <div className="row">
                        <div className="col">
                            <div className="form-group col">
                                <label for="startDate">Course Start Date</label>
                                <input
                                    onChange={e => this.setStartDate(e.target.value)}
                                    value={startDate ? startDate.substr(0, 10) : null}
                                    type="date" className="form-control"
                                    id="startDate"
                                    name="dob"
                                    onBlur={e => this.setStartDate(e.target.value)}
                                    placeholder={startDate ? startDate.substr(0, 10) : ""} />

                            </div>
                        </div>
                        <div className="col">
                            <div className="form-group col">
                                <label for="inputAddress2">Course End Date</label>
                                <input
                                    onChange={e => this.setEndDate(e.target.value)}
                                    value={endDate ? endDate.substr(0, 10) : null}
                                    type="date" className="form-control"
                                    //onfocus={e=> this.setDob(e.target.selected)}
                                    id="inputAddress2"
                                    name="dob"
                                    onBlur={e => this.setEndDate(e.target.value)}
                                    placeholder={endDate ? endDate.substr(0, 10) : ""} />

                            </div>
                        </div>

                    </div>
                    <div className="row">
                        <div className="col">
                            <div className="form-group col">
                                <label for="inputAddress2">Start Time</label>
                                <input
                                    onChange={e => this.setStartTime(e.target.value)}
                                    value={this.state.startTime}
                                    type="time" className="form-control"
                                    //onfocus={e=> this.setDob(e.target.selected)}
                                    id="inputAddress2"
                                    name="dob"
                                    placeholder={this.props.course.time} />

                            </div>
                        </div>
                        <div className="col">
                            <div className="form-group col">
                                <label for="inputAddress2">Room Number</label>
                                {/* Turn this into a series of checkboxes */}
                                <input
                                    onChange={e => this.setRoomNumber(e.target.value)}
                                    value={this.state.roomNumber}
                                    type="text" className="form-control"
                                    id="inputAddress2"
                                    name="courseMeeting"
                                    placeholder={this.props.course.room} />

                            </div>
                        </div>
                    </div>
                    <div className="row">
                        <div className="form-group col w-100">
                            <label htmlFor="teacher">Teacher</label>
                            <select className="custom-select mr-sm-2" id="teacher-id"
                                onChange={e => this.setTeacher(e.target.value)} name="teacher">
                                {/* {teacherList} */}
                            </select>

                        </div>
                        <div className="form-group col">
                            <label for="inputAddress2">Class Name</label>
                            {/* Turn this into a series of checkboxes */}
                            <input
                                onChange={e => this.setName(e.target.value)}
                                //value={this.state.name}
                                type="text" className="form-control"
                                id="inputAddress2"
                                name="courseMeeting"
                                placeholder={this.props.course.name}
                                 />

                        </div>

                    </div>


                    <div className="row">
                        <div className="col">
                            <h6 className="mb-3">Class Meet Days:</h6>
                            <div className="form-group row">

                                <div className="form-check form-check-inline">
                                    <input onClick={e => this.setCourseMeeting(e.target.value)}
                                        value={"M"} type="checkbox" className="form-check-input" id="M" name="courseMeeting" />
                                    <label className="form-check-label" for="M">M</label>
                                </div>
                                <div className="form-check form-check-inline">
                                    <input onClick={e => this.setCourseMeeting(e.target.value)}
                                        value={"T"} type="checkbox" className="form-check-input" id="T" name="courseMeeting" />
                                    <label className="form-check-label" for="T">T</label>
                                </div>
                                <div className="form-check form-check-inline">
                                    <input onClick={e => this.setCourseMeeting(e.target.value)}
                                        value={"W"} type="checkbox" className="form-check-input" id="W" name="courseMeeting" />
                                    <label className="form-check-label" for="W">W</label>
                                </div>
                                <div className="form-check form-check-inline">
                                    <input onClick={e => this.setCourseMeeting(e.target.value)}
                                        value={"Th"} type="checkbox" className="form-check-input" id="Th" name="courseMeeting" />
                                    <label className="form-check-label" for="Th">Th</label>
                                </div>
                                <div className="form-check form-check-inline">
                                    <input onClick={e => this.setCourseMeeting(e.target.value)}
                                        value={"F"} type="checkbox" className="form-check-input" id="F" name="courseMeeting" />
                                    <label className="form-check-label" for="F">F</label>
                                </div>

                            </div>
                        </div>


                    </div>
                    <button type="submit" className="btn btn-primary w-100">Add Class Details</button>



                </form>
            </div>
        );
    }
}

const mapStateToProps = state => ({
    teachers: state.teachers,
    editCourse: state.temp.editCourse
})

export default connect(mapStateToProps, {})(EditCourseForm);
