import React, { Component } from 'react';
import UserInformation from './UserInformation';
import AssignmentBox from './../../../utility-boxes/assignments/AssignmentBox';
import { TeacherBoxList, StudentBoxList, ParentBoxList, AdminBoxList } from './../../../../data/BoxList';

class ManageViewModal extends Component {

  boxListPicker(type) {
    switch (type) {
      case "STUDENT":
        return StudentBoxList;
        break;
      case "PARENT":
        return ParentBoxList;
        break;
      case "TEACHER":
        return TeacherBoxList;
        break;
      case "ADMIN":
        return AdminBoxList;
        break;
      default:
        break;
    }
  }

  render() {
    //create object to store box properties as well as a boolean, then map that array to display on the screen
    const currentUser = sessionStorage.getItem("user");
    const tempBoxList = localStorage.getItem(currentUser) ? localStorage.getItem(currentUser) : this.boxListPicker(this.props.user.type);
    const userBoxList = (
              <form className="form-inline">
                <label className="sr-only" htmlFor="inlineFormInputName2">Name</label>
                <input type="text" className="form-control mb-2 mr-sm-2" id="inlineFormInputName2" placeholder="" disabled/>
                <label className="sr-only" htmlFor="inlineFormInputGroupUsername2">Username</label>
                <div className="form-check mb-2 mr-sm-2">
                  <input className="form-check-input" type="checkbox" id="inlineFormCheck" />
                  <label className="form-check-label" htmlFor="inlineFormCheck">
                    Remember me
                  </label>
                </div>
              </form>

    )
    // const userBoxList = tempBoxList.localeCompare(box => {


    //   return box.show ? box.box : null;
    // })

    return (
      <div className="modal fade" id="manageView" tabIndex={-1} role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
        <div className="modal-dialog" role="document">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title" id="exampleModalLabel">Manage Views</h5>
              <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                <span aria-hidden="true">×</span>
              </button>
            </div>
            <div className="modal-body">
              

            </div>
            <div className="modal-footer">
              <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
              <button type="button" className="btn btn-primary">Save changes</button>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default ManageViewModal;
