import React, { Component } from 'react';

import UserHomeLayout from '../../layout/UserHomeLayout';
//import './teacher_page.css';
import Header from './../../components/main_page/header/Header';
import axios from 'axios';
import Rotate from 'react-reveal/Rotate'

class TeacherPage extends Component {
    constructor() {
        super()

        this.state = {
            user: "",
            message: "",
            courseList: [],
            userList: []
        }
    }

    componentWillMount() {
        this.props.getAdminUser(sessionStorage.getItem("user"));
        this.props.getAllStudents()
        this.props.getAllParents()
        this.props.getAllTeachers()
        this.props.getAllCourses()
    }

    updateCourseList(tempCourseList) {
        this.setState({
            courseList: tempCourseList
        })
    }

    updateUserList(tempUserList) {
        this.setState({
            userList: tempUserList
        })
    }

    componentDidMount() {

        axios.get("http://localhost:8080/course/all").then(response => {
            if (Array.isArray(response.data)) {
                this.setState({
                    courseList: response.data
                })
            }
        })

        axios.get("http://localhost:8080/all").then(response => {
            if (Array.isArray(response.data)) {
                this.setState({
                    userList: response.data
                })
            }
            
        })


    }

    componentWillMount() {
        const email = sessionStorage.getItem("user")
        const user = {
            email: email
        }

        axios.post("http://localhost:8080/find", user).then((response) => {
            console.log(response.data)
            if (response.data != "") {
                this.setState({
                    user: response.data
                })
            } else {
                sessionStorage.clear()
                this.setState({
                    message: "Error retrieving data, please logout and try again"
                })
            }
        }
        )

        
    }
        

            //Look into conditional rendering of Layouts here based on user type
    render() {
        return (
            <div className="">
       
       <div className="">
                <Header
                courseList={this.state.courseList}
                updateCourseList={(courseList)=> this.updateCourseList(courseList)} 
                updateUserList={(userList)=> this.updateUserList(userList)} 
                userList={this.state.userList} 
                user={this.state.user} />
                
                {this.state.user ? 
                <UserHomeLayout 
                userList={this.state.userList} 
                user={this.state.user} /> : <h3>{this.state.message}</h3>}
            </div>
          
                
                {/* <div class="alert alert-warning" role="alert">
                    {this.state.message}
                </div> */}
            </div>
        );
    }
}

export default TeacherPage;
