import React, { Component } from 'react';
import './modal.css'

class EditUser extends Component {
    constructor(props) {

        super()
        //this.handleSubmit = this.handleSubmit.bind(this);

        this.state = {
            dob: "",
            firstName: "",
            lastName: "",
            email: "",
            password: "",
            address: ""
        }
    }

    handleSubmit(event) {
        event.preventDefault();
        const user = {
            firstName: this.state.firstName,
            lastName: this.state.lastName,
            dob: this.state.dob,
            email: this.state.email,
            password: this.state.password,
            type: this.state.type ? this.state.type : document.getElementById("type").value,
            contactInformation: [{
                address: this.state.address,
                city: this.state.city,
                state: this.state.state,
                zipcode: this.state.zipcode
            }]
        }
        this.props.handleSubmit(user)
        document.getElementById("form-to-clear").reset();
    }

    setDob(value) {
        this.setState({
            dob: value
        })
    }

    setEmail(value) {

        this.setState({
            email: value
        })
    }

    setPassword(value) {
        this.setState({
            password: value
        })
    }

    setFirstName(value) {
        this.setState({
            firstName: value
        })
    }

    setLastName(value) {
        this.setState({
            lastName: value
        })
    }

    setAddress(value) {
        this.setState({
            address: value

        })
    }

    setUnit(value) {
        this.setState({

            unit: value

        })
    }

    setCity(value) {
        this.setState({

            city: value

        })
    }

    setUserState(value) {
        this.setState({

            state: value

        })
    }

    setZipcode(value) {
        this.setState({

            zipcode: value

        })
    }

    setType(value) {

        this.setState({
            type: value
        })
    }


    componentDidUpdate() {

        console.log(this.state.email)
    }

    componentDidMount() {
        this.setState({
            email: this.props.user.email,
            firstName: this.props.user.firstName
        })

    }


    render() {

        const Addresses = this.props.user.contactInformation && this.props.user.contactInformation.map(item => {
            return <option>{item.address} {item.unit} {item.city}, {item.state} {item.zipcode}</option>
        })

        return (
            <div>
                <div data-backdrop="static" className="modal fade modal-bg-color" id="editUser" tabIndex={-1} role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                    <div className="modal-dialog mw-75 w-75" role="document">
                        <div className="modal-content">
                            <div className="modal-header" ref={this.focusRef}>
                                <h5 className="modal-title" id="exampleModalLabel">Add or Remove Users</h5>
                                <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                                    <span aria-hidden="true">Ã—</span>
                                </button>
                            </div>
                            <div className="modal-body p-5">
                                <form onSubmit={this.handleSubmit} id="form-to-clear">
                                    <div className="form-row w-100">
                                        <div className="form-group col-md-6 w-100">
                                            <label htmlFor="email">Email address</label>
                                            <input
                                                onChange={e => this.setEmail(e.target.value)}
                                                type="email"
                                                name="email"
                                                className="form-control w-100"
                                                id="email"
                                                aria-describedby="emailHelp"
                                                value={this.state.email ? this.state.email : null}
                                                placeholder={this.props.user.email ? this.props.user.email : "Enter email"}
                                                required />

                                        </div>
                                        <div className="form-group col-md-6 w-100">
                                            <label htmlFor="password">Password</label>
                                            <input
                                                onChange={e => this.setPassword(e.target.value)}
                                                type="password" name="password"
                                                className="form-control w-100"
                                                id="password"
                                                value={this.state.password ? this.state.password : null}
                                                placeholder={this.props.user.password ? this.props.user.password : "Password"}
                                                required />

                                        </div>
                                    </div>

                                    <div className="form-row w-100">

                                        <div className="form-group col-md-6 w-100">
                                            <label htmlFor="password">First Name</label>
                                            <input required
                                                value={this.state.firstName ? this.state.firstName : null}
                                                onChange={e => this.setFirstName(e.target.value)}
                                                type="text"
                                                name="firstName"
                                                className="form-control w-100"
                                                id="firstName"
                                                aria-describedby="emailHelp"
                                                placeholder={this.props.user.firstName ? this.props.user.firstName : "First Name"}
                                                required />
                                        </div>

                                        <div className="form-group col-md-6 w-100">
                                            <label htmlFor="password">Last Name</label>
                                            <input required
                                                value={this.state.lastName ? this.state.lastName : null}
                                                onChange={e => this.setLastName(e.target.value)}
                                                type="text" name="lastName"
                                                className="form-control w-100"
                                                id="lastName"
                                                placeholder={this.props.user.lastName ? this.props.user.lastName : "Last Name"} />
                                        </div>
                                    </div>

                                    {/* Dates not working correctly yet */}
                                    <div className="form-group col">
                                        <label for="inputAddress2">Date of Birth</label>
                                        {console.log(this.props.user.dob)}
                                        <input required
                                            value={this.state.dob ? Date(this.state.dob) : null}
                                            onChange={e => this.setDob(e.target.value)}
                                            type="date" className="form-control"
                                            id="inputAddress2"
                                            name="unit"
                                            placeholder={this.props.user.dob ? Date(this.props.user.dob) : ""} />

                                    </div>

                                    <div class="input-group col">
                                        <div class="input-group-prepend">
                                            <button class="btn btn-outline-primary" type="button" id="button-addon1">Edit</button>
                                        </div>
                                        <select id="type" onChange={e => this.setType(e.target.value)} className="custom-select form-control" name="type" required>
                                            {Addresses}
                                        </select>
                                    </div>

                                    <div>
                                        {/* Create a hidden drow down for editing an address here */}
                                    </div>


                                    <div className="form-row">
                                        <div className="col">
                                            <input type="submit" className="btn btn-primary w-100 mt-4">
                                            </input>
                                        </div>
                                    </div>
                                </form>
                            </div>
                            <div className="modal-footer">
                                <h4 className="text-success mr-5">{this.state.success}</h4>
                                <button type="button" className="btn btn-secondary" data-dismiss="modal" >Close</button>
                            </div></div>
                    </div>
                </div>
            </div>

        );
    }
}

export default EditUser;
