import React, { Component } from 'react';
import UserHomeLayout from '../../layout/UserHomeLayout';
import Header from './../../components/main_page/header/Header';
import { PropTypes } from 'prop-types';
import { connect } from 'react-redux';
import { getTeacherUser } from './../../action/teacherActions';

class TeacherPage extends Component {
    constructor() {
        super()

        this.state = {
            user: "",
            message: "",
            courseList: [],
            userList: []
        }
    }

    componentWillMount() {
        this.props.getAdminUser(sessionStorage.getItem("user"));
        this.props.getAllStudents()
        this.props.getAllCourses()
    }

    //Look into conditional rendering of Layouts here based on user type
    render() {
        return (


            <div className="">
                <Header type="TEACHER" />


                <UserHomeLayout type="TEACHER" />


            </div>
        );
    }
}

TeacherPage.prototypes = {
    students: PropTypes.array.isRequired,
    parents: PropTypes.array.isRequired,
    teachers: PropTypes.array.isRequired
}

export default connect(null, { getTeacherUser, getAllStudents, getAllTeachers, getAllParents, getAllCourses })(TeacherPage);
