import axios from 'axios'
import types from '../../'

const getUser = () => {
    axios.get(`http://localhost:8080/user/find/${sessionStorage.getItem("user")}`).then(response => {
        return response.data
    })
}

const userReducer = (state = {}, action) => {
    switch (action.type) {
        case 'SET_USER':
            return action.payload
        case 'GET':
            return state;
        case 'REMOVE':
            return state;
        default:
            return state;
    }
}

export default userReducer;
