import './admin_page.css';
import UserHomeLayout from '../../layout/UserHomeLayout';
import axios from 'axios';
import { useDispatch } from 'react-redux';
import { getAllStudents } from '../../action/student/studentAction'

getUser = async (id) => {
        await axios.get("http://localhost:8080/admin/find/" + id,).then((response) => {
        console.log("IN")
        response.data ? setUser(response.data) : setMessage(response.data);
});}

function AdminPage() {

    useDispatch(getAllStudents());

    const [user, setUser] = useState({});
    const [message, setMessage] = useState("");
    const setUserMethod = (data) => setUser(data);
