import React, { Component } from 'react';
import Checkmark from '../../../images/icons/checkmark.svg';
import Trash from '../../../images/icons/trash.svg';

class ToDoItem extends Component {
  constructor() {
    super()

    this.handleClick = this.handleClick.bind(this)

    this.state = {
      complete: false
    }
  }

  handleClick() {

  }

  render() {
    //All events need to be functions
    let completeStyle = {
      transitionDuration: '1s'
    }

    if (this.state.complete) {
      completeStyle = {
        opacity: .4,
        textDecoration: "line-through",
        transitionDuration: '1s'
      }
    }

    return (
      <div className="card p-2 col-md-3 m-3 rounded shadow" style={completeStyle}>
        <blockquote className="blockquote mb-0 card-body text-smaller" style={completeStyle}>
          {this.props.text}
        </blockquote>
        <div className="row">
          <div className="col">
            
            <button 
            onClick={this.handleClick} 
            className="btn btn-primary w-100"><span style={completeStyle}><img src={Checkmark} /></span></button>
          </div>
          <div className="col">
            <button onClick={() => this.props.remove(this.props.text)} className="btn btn-info w-100"><img src={Trash} /></button>
          </div>


        </div>
      </div>
    );
  }
}

export default ToDoItem;
