import Header from '../../components/main_page/header/Header';
import './admin_page.css';
import UserHomeLayout from '../../layout/UserHomeLayout';
import React, { useState, useEffect, Component } from 'react';
import { connect } from 'react-redux'
import { getAdminUser } from './../../action/user/userActions'; //import the action
import { getAllStudents } from './../../action/student/studentActions'
import { getAllTeachers } from './../../action/teacher'
class AdminPage extends Component {

    componentWillMount() {
        //Action to set the state of the user
        this.props.getAdminUser(sessionStorage.getItem("user"));
        this.props.getAllStudents()
        this.props.getAllTeachers()
        this.props.getAllParents()
    }

    render() {
        return (
            <div className="">
            <Header
                type="ADMIN" />
            {/* <UserHomeLayout 
                    type="ADMIN" /> */}
        </div>
        );
    }
}

const mapStateToProps = state => ({
    user: state.user,
    students: state.students,
    parents: state.parents,
    teachers: state.teachers
})

//connects our component to the store
export default connect(mapStateToProps, { getAdminUser, getAllStudents, getAlLTeachers, getAllParents })(AdminPage);
