import React, { Component } from 'react';

class GradeItem extends Component {
    render() {
        let color = null;

        

        if (letterGrade == "A") {
            color = "bg-success text-light"
        } else if (letterGrade === "B") {
            color = "bg-info text-light"
        } else if (letterGrade === "C") {
            color = "bg-warning"
        } else {
            color = "bg-danger"
        }


        let gradeCardStyle = "card mx-1 my-1 " + color
        

        return (
                <div className={gradeCardStyle} style={{width: '10rem'}}>
                  <div className="card-body">
                    <h5 className="card-title">{this.props.grade.className}</h5>
                    <h6 className="card-subtitle mb-2">{this.props.grade.letterGrade}</h6>
                    <p className="card-text">
                      Percent: {this.props.grade.percent} <br></br>
                      Class Average: {this.props.grade.average}
                    </p>
                    <a href="/#" className="btn btn-secondary">Go to class</ a>
                  </div>
                </div>

        );
    }
}

export default GradeItem;
