import React, { Component } from 'react';
import './messaging.css';
import { connect } from 'react-redux'
import RecipientItem from './RecipientItem';

class WriteMessage extends Component {

  filterList(list, type) {
    if (Array.isArray(list)) {
      return list.filter(user => {
        if (this.checkUser(user.user.firstName, user.user.lastName) && this.typeCheck(type)) {
          return user
        }
      })
    }  
}

  checkUser(firstName, lastName) {
  const length = this.state.search.length
  if (this.state.search.toLowerCase() == firstName.toLowerCase().substr(0, length) || 
  this.state.search.toLowerCase() == lastName.toLowerCase().substr(0, length)) {
    return true
  } else {
    return false
  }
}


  render() {
    let users = []
    if (this.props.users) {
      users = this.props.users.map(user => {
        return <RecipientItem firstName={user.firstName} lastName={user.lastName} id={user.id}/>
      })
    }

    const studentComponentList = this.filterList(studentList, "student") && this.filterList(studentList, "student").map(student => {
      const fullName = student.user.firstName + " " + student.user.lastName
      return <Bounce key={student.id}><UserListing id={student.id} currentUser={student} type="student" text={fullName}/></Bounce>
  
    })



    return (
      <div>
        <div className="modal fade write-bg" id="writeMessage" data-backdrop="static" data-keyboard="false" tabIndex={-1} role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
          <div className="modal-dialog modal-dialog-centered mw-100 w-50" role="document">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title" id="exampleModalLabel">Compose Message</h5>
                
                <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                  <span aria-hidden="true">×</span>
                </button>
              </div>
              <div className="modal-body">
                <div className="row">
                  <div className="col-md-3">
                  
                    {users}
                  </div>
                  <div className="col">
                    <form>
                    <input type="text" class="form-control" placeholder="Recipient's username" />
                        
              

                      <input type="text" className="form-control my-1" id="formGroupExampleInput2" placeholder="Subject" />
                      <textarea className="form-control my-1" rows={5} defaultValue={"Enter text here"} />
                    </form>
                  </div>

                </div>

              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-secondary mr-auto" data-dismiss="modal">Cancel</button>
                <button type="button" className="btn btn-info">Save</button>
                <button type="button" className="btn btn-primary w-25">Send</button>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => ({
  users: state.user.users
})

export default connect(mapStateToProps, {})(WriteMessage);
