import React, { Component } from 'react';
import axios from 'axios'
import CourseItem from '../addCourse/CourseItem';
import Bounce from 'react-reveal/Bounce'
import AssignStudentForm from './AssignStudentForm';
import StudentItem from './StudentItem';

class AssignStudentModal extends Component {
    constructor() {
        super()
        this.state = {
            search: "",
            selectedCourse: {},
            studentList: []
        }
    }

    selectStudent(checked, value) {
        console.log(value)
        console.log(checked)

        if (checked) {
            let tempList = this.state.studentList
            tempList.push(value)
            this.setState({
                studentList: tempList
            })
        } else {
            let tempList = this.state.studentList
            tempList.indexOf(value)
            tempList.filter(student => {
                if (student.id == value.id) {
                    tempList.splice()
                }
            })
        }

    }

    handleSearchChange(value) {
        this.setState({
            search: value
        })
    }

    render() {
        let counter = 1
        const courseItems = this.props.courseList.map(course => {
            return <CourseItem course={course} name={course.name} selectCourse={() => this.editCourse(course)} />
        })

        let studentItems = []

        this.props.userList.filter(student => {
            if (student.type == "STUDENT") {
                studentItems.push(<StudentItem selectStudent={e => this.selectStudent(e.target.checked, student)} student={student} name={student.firstName + " " + student.lastName} />)
            }
        })
        
        console.log(studentItems)

        return (
            <div>
                <div className="modal fade" id="assignStudentModal" tabIndex={-1} role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                    <div className="modal-dialog mw-100 w-75" role="document">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title" id="exampleModalLabel">Assign Students</h5>
                                <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                                    <span aria-hidden="true">×</span>
                                </button>
                            </div>
                            <div className="modal-body">

                                <div>
                                    <div className="row pl-2 border-bottom">

                                        {/* Top Search Bar */}

                                        <form className="form-inline ml-auto mr-2" >
                                            <div className="input-group mb-2 mr-sm-2">
                                                <div className="input-group-prepend">

                                                    <div className="input-group-text">Search</div>
                                                </div>
                                                <input onChange={e => this.handleSearchChange(e.target.value)} type="text" className="form-control" id="inlineFormInputGroupUsername2" placeholder="Username" />
                                            </div>
                                        </form>



                                    </div>
                                    <div className="row">
                                        <div className="col-md-3 border-right overflow-auto mt-4">
                                            <h5>Courses</h5>
                                            <fieldset className="form-group">

                                                {/* List of all courses that have been searched for */}

                                                {courseItems}


                                            </fieldset>
                                        </div>

                                        <div className="col mt-4">

                                            <div className="col-md-12 pl-3">


                                                <Bounce top>
                                                    {/* Form to create a new course */}

                                                    <div id="assignStudents" style={{ display: "none" }}>
                                                        <AssignStudentForm course={this.state.selectedCourse} selectedStudents={this.state.studentList} />
                                                    </div>



                                                </Bounce>
                                            </div>
                                        </div>
                                        <div className="col-md-3 border-left overflow-auto mt-4">
                                            <h5>Students</h5>
                                            <fieldset className="form-group">

                                                {studentItems === 0 ? (
                                                    <div class="progress">
                                                        <div class="progress-bar progress-bar-striped progress-bar-animated w-75"
                                                            role="progressbar"
                                                            aria-valuenow={() => counter++} aria-valuemin="0" aria-valuemax="100" ></div>
                                                    </div>
                                                ) : (
                                                        studentItems.map(student => {
                                                            const length = this.state.search.toLowerCase().length
                                                            const firstName = student.props.student.firstName.substr(0, length).toLowerCase()
                                                            const lastName = student.props.student.lastName.substr(0, length).toLowerCase()
                                                            if (
                                                                (((this.state.search.toLowerCase() == firstName)) ||
                                                                    (this.state.search.toLowerCase() == lastName))) {

                                                                return student
                                                            }
                                                        }
                                                        ))}

                                            </fieldset>
                                        </div>
                                    </div>
                                </div>

                            </div>
                            <div className="modal-footer">
                                <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                                <button type="button" className="btn btn-primary">Save changes</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default AssignStudentModal;
