import React, { Component } from 'react';
import axios from 'axios'

class AddCourseForm extends Component {
    constructor() {
        super()
        this.handleSubmit = this.handleSubmit.bind(this);
        this.state = {
            states: [],
            dob: "",
            firstName: "",
            lastName: "",
            email: "",
            password: "",
            address: "",
            unit: "",
            city: "",
            state: "",
            zipcode: "",
            type: "".toUpperCase(),
            tempUser: {}
        }
    }

    setDob(value) {
        this.setState({
            dob: value
        })
    }

    setEmail(value) {
        this.setState({
            email: value
        })
    }

    setPassword(value) {
        this.setState({
            password: value
        })
    }

    setFirstName(value) {
        this.setState({
            firstName: value
        })
    }

    setLastName(value) {
        this.setState({
            lastName: value
        })
    }

    setAddress(value) {
        this.setState({
            address: value

        })
    }

    setUnit(value) {
        this.setState({

            unit: value

        })
    }

    setCity(value) {
        this.setState({

            city: value

        })
    }

    setUserState(value) {
        this.setState({

            state: value

        })
    }

    setZipcode(value) {
        this.setState({

            zipcode: value

        })
    }

    setType(value) {

        this.setState({
            type: value
        })
    }

    handleSubmit (event) {
        event.preventDefault();
        const user = {
            firstName: this.state.firstName,
            lastName: this.state.lastName,
            dob: this.state.dob,
            email: this.state.email,
            password: this.state.password,
            type: this.state.type ? this.state.type : document.getElementById("type").value,
            contactInformation: [{
              address: this.state.address,
              city: this.state.city,
              state: this.state.state,
              zipcode: this.state.zipcode
            }]
          }
        this.props.handleSubmit(user)
        document.getElementById("form-to-clear").reset();
    }

    componentDidMount() {
        
        axios.get("http://localhost:8080/getStates").then(response => {
            const temp = response.data.map(st => {
                return <option>{st}</option>
            })

            this.setState({
                states: temp
            })
        })
    }

    render() {
        return (
            <div>
                <form id="form-to-clear" onSubmit={this.handleSubmit}>
                    <div className="form-row w-100">
                        <div className="form-group col-md-6 w-100">
                            <label htmlFor="title">Course Title</label>
                            <input onChange={e => this.setEmail(e.target.value)} type="text" name="title" className="form-control w-100" id="email" aria-describedby="emailHelp" placeholder="Enter email" required />
                 
                        </div>
                        <div className="form-group col-md-6 w-100">
                        <label htmlFor="teacher">Teacher</label>
                            <select className="custom-select mr-sm-2" name="teacher">
                              <option selected>Choose...</option>
                              <option value={1}>One</option>
                              <option value={2}>Two</option>
                              <option value={3}>Three</option>
                            </select>
            
                        </div>
                    </div>
                    <div className="form-row w-100">
                        <button type="submit" className="btn btn-outline-primary">Create New Course</button>
                    </div>
                </form>
            </div>
        );
    }
}

export default AddCourseForm;
