import React, { Component } from 'react';
import { HotTable } from '@handsontable/react'
import 'handsontable/dist/handsontable.full.css';
import 'handsontable/dist/handsontable.full.css'
import { connect } from 'react-redux';

class Table extends Component {
    constructor() {
        super()
        this.state = {

        }
        this.data = [
            
        ]
    }

    componentDidUpdate() {
        let columnLabels = []
        let colHeaders = this.props.editCourse && this.props.editCourse.assignments.map(assignment => {
            columnLabels.push({data: assignment.id})
            return assignment.name + " (" + assignment.totalPoints + ")"
        })
        
        let rowLabels = []
        let rowHeaders = this.props.editCourse && this.props.editCourse.studentList.map(student => {
            rowLabels.push({data: student.id})
            return student.user.firstName + " " + student.user.lastName
        })

        let studentData = this.props.editCourse && this.props.editCourse.studentList.map(student => {
            let tempArray = []
            student.assignmentScores && student.assignmentScores.forEach(score => {
                if (score === undefined || score === null) {
                    tempArray.push('')
                } else {
                    tempArray.push(score.points)
                }
                
            });
            return tempArray
        })

        this.setState({
            colLbl: columnLabels,
            colHead: colHeaders,
            rowLbl: rowLabels,
            data: studentData
        })
    }

    render() {
    
        this.data = this.state.data

        const settings = {
            licenseKey: 'non-commercial-and-evaluation',
            data: this.data,
            colHeaders: this.state.colHead,
            rowHeaders: this.state.rowHead,
            rowHeaderWidth: 150,
            columns: this.state.colLbl,
            row: this.state.rowLbl,
            // width: "600", 
            // height: "300",
            headerTooltips: {
                rows: true,
                columns: true,
                onlyTrimmed: false
            }
        }

        return (
            <div id="hot-app" className="table table-hover table-sm">
                <HotTable id="example1" className="my-3" settings={settings} />
            </div>
        );
    }
}

const mapStateToProps = state => ({
    editCourse: state.temp.editCourse,
    temp: state.temp.grid,
})

export default connect(mapStateToProps, {})(Table)
