import React, { Component } from 'react';
import Delete from '../../../../images/icons/trash.svg'
import Edit from '../../../../images/icons/edit.svg'
import { connect } from 'react-redux';
import { removeTeacher } from '../../../../action/teacher/teacherActions'

class UserListing extends Component {

    deleteUser(name, type, id) {
        //simply delete the user from here, show an alert box
        if(window.confirm("Are you sure you want to delete " + name + "?"))
        switch (type) {
            case 'teacher':
                this.props.removeTeacher(id)
                break;
            case 'parent':
                
                break;
            case 'student':
                break;
            default:
                break;
        }
    }

    editUser() {
        //Set the user into state to be used
    }

    render() {
        const iconStyle = {
            width: 18,
            height: 18
        }
        return (
            <div>
            <form className="form-inline">

                <a
                    title={this.props.text}
                    onClick={() => this.deleteUser(this.props.text, this.props.type, this.props.key)}
                    className="mx-2">
                    <img style={iconStyle} src={Delete} /></a>

                <a
                    data-toggle="modal"
                    data-target="#editUser"
                    onClick={this.editUser}
                    className="mx-2">
                    <img style={iconStyle} src={Edit} /></a>
                <span className="text-muted mx-2">{this.props.text}</span>
            </form>

        </div>
        );
    }
}



export default connect(null, { removeTeacher })(UserListing);
