import React, { Component } from 'react';

import TeacherLayout from './TeacherLayout';
//import './teacher_page.css';
import Header from './../../components/main_page/header/Header';
import AlertModal from './../../components/AlertModal';

class TeacherPage extends Component {
    constructor() {
        super()
        this.retireveUserData = this.retireveUserData.bind(this)
        this.state = {
            user: "",
            message: ""
        }
    }

    retireveUserData(email) {
        axios.get("http://localhost:8080/teacher/" + email)
            .then((response) => {
                if (response.data != "") {
                    this.setState({
                        user: response.data
                    })
                } else {
                    sessionStorage.clear()
                    this.setState({
                        message: "Error retrieving data, please try again"
                    })
                }
            }
            )}
        


    render() {

        //console.log(sessionStorage.getItem('user')) - this is the email of the user, used to retrieve data



        return (
            <div className="">
                <Header />
                <TeacherLayout />
                <AlertModal />
            </div>
        );
    }
}

export default TeacherPage;
