import React, { Component } from 'react';
import { connect } from 'react-redux';


class TablePractice extends Component {
  render() {
    const colHeaders = this.props.editCourse && this.props.editCourse.assignments.map(assignment => {
      return (
      <th 
        className="text-center"
        key={assignment.id} 
        id={assignment.id}
        //style={{width: '10%'}} 
        title={assignment.name + "-" + assignment.totalPoints} 
        scope="col">{assignment.name} <br/> {assignment.totalPoints}</th>
        )
    })

    console.log(this.props.editCourse)
    const rows = this.props.editCourse && this.props.editCourse.studentList.map(student => {
      
      // for (let index = 0; index < student.assignments.length; index++) {
        

      // }

      return (
      <tr>
        <th scope="row">{student.user.firstName} {student.user.lastName}</th>
        <tr></tr>
      </tr>)

    })



    return (
      <div className="my-2">
        <table class="table table-hover table-sm">
          <thead className="thead-light">
            <tr>
              <th scope="col"></th>
              {colHeaders}
            </tr>
          </thead>
          <tbody>
            {rows}

          </tbody>
        </table>
      </div>
    );
  }
}


const mapStateToProps = state => ({
  editCourse: state.temp.editCourse,
  temp: state.temp.grid,
})

export default connect(mapStateToProps, {})(TablePractice);
