import { GET_USER } from '../types'
import axios from 'axios'


export function fetchPosts() {
    return function (dispatch) {
        //all fetch statements go here
    }
}

//dispatch is part of thunk, it sends the data off to the reducer
export const getAdminUser = (id) => dispatch => {
    axios.get("http://localhost:8080/admin/find/" + id)
        .then(response => {
            console.log("DISPATCH")
            dispatch({
                type: GET_USER,
                payload: response.data
            })
        })
}

export const getStudentUser = (id) => dispatch => {
    axios.get("http://localhost:8080/student/find/" + id)
        .then(response => {
            console.log("DISPATCH")
            dispatch({
                type: GET_USER,
                payload: response.data
            })
        }

        )
}

export const getParentUser = (id) => dispatch => {
    axios.get("http://localhost:8080/student/find/" + id)
        .then(response => {
            console.log("DISPATCH")
            dispatch({
                type: GET_USER,
                payload: response.data
            })
        }

        )
}


export const increment = (num) => {
    return {
        type: 'INCREMENT',
        payload: num
    }
}

export const setUser = (user) => {
    return {
        type: 'SET_USER',
        payload: user
    }
}
