import React, { Component } from 'react';

class AddItem extends Component {
    constructor() {
        super()
        this.state = {
            text: ''
        }
    

    this.updateInput = this.updateInput.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

    }

    updateInput(event){
    this.setState({text : event.target.value})
    }

    handleSubmit() {



    }

    render() {
        return (
            <div>
                <div className="modal fade" id="addItemModal" tabIndex={-1} role="dialog" aria-labelledby="exampleModalCenterTitle" aria-hidden="true">
                  <div className="modal-dialog modal-dialog-centered" role="document">
                    <div className="modal-content">
                      <div className="modal-header">
                        <h5 className="modal-title" id="exampleModalLongTitle">  Add Item to List</h5>
                        <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                          <span aria-hidden="true">Ã—</span>
                        </button>
                      </div>
                      <div className="modal-body">
                        <input value={this.state.text} onChange={this.updateInput} type="text" className="form-control mb-2 mr-sm-2" placeholder="To Do List Item" />
                        
                      </div>
                      <div className="modal-footer">
                        <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                        <button onClick={this.handleSubmit} type="button" className="btn btn-primary">Add Item</button>
                      </div>
                    </div>
                  </div>
                </div>
            </div>
        );
    }
}

export default AddItem;
